import React, { useState, useEffect } from 'react';
import './App.css';
import { FlashSaleStatus, PurchaseResult } from './types';
import { FlashSaleAPI } from './services/api';

function App() {
  const [status, setStatus] = useState<FlashSaleStatus | null>(null);
  const [userId, setUserId] = useState('');
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<{ hasPurchased: boolean; purchaseId?: string } | null>(null);

  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchStatus = async () => {
    try {
      const statusData = await FlashSaleAPI.getStatus();
      setStatus(statusData);
    } catch (error) {
      console.error('Failed to fetch status:', error);
    }
  };

  const handlePurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    setLoading(true);
    try {
      const result = await FlashSaleAPI.attemptPurchase(userId);
      setPurchaseResult(result);

      if (result.success) {
        setUserPurchaseStatus({ hasPurchased: true, purchaseId: result.purchaseId });
      }

      fetchStatus();
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'An error occurred during purchase',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const checkUserPurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    try {
      const result = await FlashSaleAPI.checkUserPurchase(userId);
      setUserPurchaseStatus(result);
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const formatTime = (milliseconds: number | undefined) => {
    if (!milliseconds) return '';

    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m ${seconds % 60}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600';
      case 'active': return 'text-green-600';
      case 'sold-out': return 'text-red-600';
      case 'ended': return 'text-gray-600';
      default: return 'text-gray-600';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'upcoming': return '‚è∞';
      case 'active': return 'üî•';
      case 'sold-out': return '‚ùå';
      case 'ended': return 'üèÅ';
      default: return '‚ùì';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50">
      <div className="container mx-auto px-4 py-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            üöÄ Flash Sale System
          </h1>
          <p className="text-gray-600 text-lg">
            High-Throughput Limited Edition Product Sale
          </p>
        </div>

        {status && (
          <div className="bg-white rounded-lg shadow-lg p-6 mb-8" data-testid="flash-sale-status">
            <div className="text-center mb-6">
              <div className="text-6xl mb-4">{getStatusIcon(status.status)}</div>
              <h2 className={`text-3xl font-bold ${getStatusColor(status.status)} mb-2`} data-testid="status-text">
                {status.status.charAt(0).toUpperCase() + status.status.slice(1)}
              </h2>
              <div className="text-gray-600" data-testid="time-info">
                {status.status === 'upcoming' && status.timeUntilStart && (
                  <p>Starts in: {formatTime(status.timeUntilStart)}</p>
                )}
                {status.status === 'active' && status.timeUntilEnd && (
                  <p>Ends in: {formatTime(status.timeUntilEnd)}</p>
                )}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6" data-testid="stock-info">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600" data-testid="total-stock">{status.maxStock}</div>
                <div className="text-gray-600">Total Stock</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600" data-testid="current-stock">{status.currentStock}</div>
                <div className="text-gray-600">Available</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-purple-600" data-testid="sold-stock">{status.maxStock - status.currentStock}</div>
                <div className="text-gray-600">Sold</div>
              </div>
            </div>

            <div className="text-center text-sm text-gray-500">
              <p>Start: {new Date(status.startTime).toLocaleString()}</p>
              <p>End: {new Date(status.endTime).toLocaleString()}</p>
            </div>
          </div>
        )}

        <div className="bg-white rounded-lg shadow-lg p-6 mb-8" data-testid="purchase-section">
          <h3 className="text-xl font-semibold mb-4 text-gray-800">Purchase Section</h3>

          <div className="mb-4">
            <label htmlFor="userId" className="block text-sm font-medium text-gray-700 mb-2">
              User ID
            </label>
            <input
              type="text"
              id="userId"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              placeholder="Enter your user ID (e.g., email or username)"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              data-testid="user-id-input"
            />
          </div>

          <div className="flex flex-wrap gap-4 mb-4">
            <button
              onClick={handlePurchase}
              disabled={loading || status?.status !== 'active'}
              className={`px-6 py-2 rounded-md font-medium transition-colors ${
                loading || status?.status !== 'active'
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-red-600 text-white hover:bg-red-700 focus:ring-2 focus:ring-red-500'
              }`}
              data-testid="buy-now-button"
            >
              {loading ? 'Processing...' : 'Buy Now!'}
            </button>

            <button
              onClick={checkUserPurchase}
              disabled={!userId.trim()}
              className={`px-6 py-2 rounded-md font-medium transition-colors ${
                !userId.trim()
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-blue-600 text-white hover:bg-blue-700 focus:ring-2 focus:ring-blue-500'
              }`}
              data-testid="check-purchase-button"
            >
              Check Purchase Status
            </button>
          </div>

          {purchaseResult && (
            <div className={`${
              purchaseResult.success 
                ? 'bg-green-100 border border-green-300 text-green-800' 
                : 'bg-red-100 border border-red-300 text-red-800'
            } p-4 rounded-md`}
              data-testid="purchase-result"
            >
              <div className="font-medium">{purchaseResult.message}</div>
              {purchaseResult.purchaseId && (
                <div className="text-sm mt-1">Purchase ID: {purchaseResult.purchaseId}</div>
              )}
              <div className="text-sm mt-1">{new Date(purchaseResult.timestamp).toLocaleString()}</div>
            </div>
          )}

          {userPurchaseStatus && (
            <div className={`${
              userPurchaseStatus.hasPurchased 
                ? 'bg-blue-100 border border-blue-300 text-blue-800' 
                : 'bg-gray-100 border border-gray-300 text-gray-800'
            } p-4 rounded-md`}
              data-testid="user-purchase-status"
            >
              <div className="font-medium">
                {userPurchaseStatus.hasPurchased 
                  ? `You have successfully purchased an item!` 
                  : `No purchase found for user: ${userId}`
                }
              </div>
              {userPurchaseStatus.purchaseId && (
                <div className="text-sm mt-1">Purchase ID: {userPurchaseStatus.purchaseId}</div>
              )}
            </div>
          )}
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-semibold mb-4 text-gray-800">System Information</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-600">
            <div>
              <p><strong>Backend:</strong> Node.js + Express + Redis</p>
              <p><strong>Frontend:</strong> React + TypeScript</p>
            </div>
            <div>
              <p><strong>Features:</strong> High-throughput, Concurrency Control</p>
              <p><strong>Architecture:</strong> Scalable, Fault-tolerant</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
