import React, { useState, useEffect } from 'react';
import { FlashSaleStatus, PurchaseResult, User, UserSeedResponse, Product } from './types';
import { 
  FlashSaleIcon, 
  ClockIcon, 
  CheckIcon, 
  InfoIcon, 
  WarningIcon, 
  ShoppingBagIcon, 
  StarIcon, 
  ArrowLeftIcon, 
  ArrowRightIcon 
} from './components/icons';

const App: React.FC = () => {
  const [flashSaleStatus, setFlashSaleStatus] = useState<FlashSaleStatus | null>(null);
  const [username, setUsername] = useState('john_doe');
  const [loading, setLoading] = useState(false);
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<{ hasPurchased: boolean; purchaseId?: string } | null>(null);
  const [activeTab, setActiveTab] = useState<'detail' | 'specifications' | 'important' | 'users'>('detail');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [users, setUsers] = useState<User[]>([]);
  const [usersLoading, setUsersLoading] = useState(false);
  const [product, setProduct] = useState<Product | null>(null);
  const [productLoading, setProductLoading] = useState(true);
  const [showOnboarding, setShowOnboarding] = useState(false);
  const [onboardingStep, setOnboardingStep] = useState(0);
  const [highlightedElement, setHighlightedElement] = useState<string | null>(null);
  const [resetMessage, setResetMessage] = useState<string | null>(null);

  useEffect(() => {
    fetchFlashSaleStatus();
    fetchProduct();
    const interval = setInterval(fetchFlashSaleStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (activeTab === 'users') {
      fetchUsers();
    }
  }, [activeTab]);

  const fetchFlashSaleStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/status');
      if (response.ok) {
        const data = await response.json();
        setFlashSaleStatus(data);
      }
    } catch (error) {
      console.error('Failed to fetch flash sale status:', error);
    }
  };

  const fetchProduct = async () => {
    try {
      setProductLoading(true);
      const response = await fetch('http://localhost:3001/api/flash-sale/product');
      if (response.ok) {
        const data = await response.json();
        setProduct(data);
      }
    } catch (error) {
      console.error('Failed to fetch product data:', error);
    } finally {
      setProductLoading(false);
    }
  };

  const attemptPurchase = async () => {
    if (!username.trim()) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userIdentifier: username.trim() }),
      });

      if (response.ok) {
        const result = await response.json();
        setPurchaseResult(result);
        if (result.success) {
          fetchFlashSaleStatus();
          fetchUsers(); // Refresh user list to update purchase status
        }
      } else {
        const errorData = await response.json();
        setPurchaseResult(errorData);
      }
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'Network error. Please try again.',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const checkUserPurchase = async () => {
    if (!username.trim()) return;

    try {
      const response = await fetch(`http://localhost:3001/api/flash-sale/purchase/${username.trim()}`);
      if (response.ok) {
        const data = await response.json();
        setUserPurchaseStatus(data);
      }
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const fetchUsers = async () => {
    setUsersLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/users');
      if (response.ok) {
        const data = await response.json();
        setUsers(data);
      }
    } catch (error) {
      console.error('Failed to fetch users:', error);
    } finally {
      setUsersLoading(false);
    }
  };

  const seedUsers = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/users/seed', {
        method: 'POST',
      });
      if (response.ok) {
        const data: UserSeedResponse = await response.json();
        console.log(data.message);
        fetchUsers(); // Refresh the users list
      }
    } catch (error) {
      console.error('Failed to seed users:', error);
    }
  };

  const resetUsers = async () => {
    try {
      // First reset users
      const resetResponse = await fetch('http://localhost:3001/api/users/reset', {
        method: 'DELETE',
      });

      if (resetResponse.ok) {
        setUsers([]);
        console.log('Users reset successfully');

        // Automatically seed new users after reset
        const seedResponse = await fetch('http://localhost:3001/api/users/seed', {
          method: 'POST',
        });

        if (seedResponse.ok) {
          const data = await seedResponse.json();
          console.log('Users auto-seeded successfully:', data.message);
          // Refresh the users list to show new users
          fetchUsers();
        } else {
          console.error('Failed to auto-seed users after reset');
        }
      }
    } catch (error) {
      console.error('Failed to reset users:', error);
    }
  };

  const resetSystem = async () => {
    try {
      // Reset the entire system (stock + purchases)
      const resetResponse = await fetch('http://localhost:3001/api/flash-sale/reset', {
        method: 'POST',
      });

      if (resetResponse.ok) {
        console.log('System reset successfully');

        // Automatically seed users after system reset
        const seedResponse = await fetch('http://localhost:3001/api/users/seed', {
          method: 'POST',
        });

        if (seedResponse.ok) {
          const data = await seedResponse.json();
          console.log('Users auto-seeded successfully:', data.message);

          // Refresh all data
          fetchFlashSaleStatus();
          fetchUsers();

          // Show success message
          alert('System reset successfully! Stock restored to 5 items, all purchases cleared, and users auto-seeded.');
        } else {
          console.error('Failed to auto-seed users after reset');
          alert('System reset successful, but failed to auto-seed users. Please manually seed users.');

          // Still refresh data even if seeding failed
          fetchFlashSaleStatus();
          fetchUsers();
        }
      } else {
        console.error('Failed to reset system');
        alert('Failed to reset system. Please try again.');
      }
    } catch (error) {
      console.error('Failed to reset system:', error);
      alert('Failed to reset system. Please try again.');
    }
  };

  const resetPurchaseSection = async () => {
    setUsername('');
    setPurchaseResult(null);
    setUserPurchaseStatus(null);
    setLoading(false);
  };

  const resetPurchaseSectionForUser = async (newUsername: string) => {
    // Clear previous state
    setPurchaseResult(null);
    setUserPurchaseStatus(null);
    setLoading(false);

    // Set new username
    setUsername(newUsername);

    // Check if the new user has already purchased
    if (newUsername.trim()) {
      try {
        const response = await fetch(`http://localhost:3001/api/flash-sale/purchase/${newUsername.trim()}`);
        if (response.ok) {
          const data = await response.json();
          setUserPurchaseStatus(data);
        }
      } catch (error) {
        console.error('Failed to check user purchase status:', error);
      }
    }

    // Start onboarding sequence after a short delay
    setTimeout(() => {
      // Step 1: Highlight username input and center it
      const usernameInput = document.getElementById('username-input');
      if (usernameInput) {
        // Add highlight class
        usernameInput.classList.add('onboarding-highlight');
        usernameInput.focus();

        // Force immediate scroll to center with multiple attempts
        const scrollToCenter = () => {
          const rect = usernameInput.getBoundingClientRect();
          const viewportHeight = window.innerHeight;
          const elementTop = rect.top;
          const elementHeight = rect.height;
          const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

          // Calculate exact center position
          const targetScrollTop = currentScrollTop + elementTop - (viewportHeight / 2) + (elementHeight / 2);

          // Force scroll immediately
          window.scrollTo({
            top: targetScrollTop,
            behavior: 'auto'
          });

          // Then smooth scroll for better UX
          setTimeout(() => {
            window.scrollTo({
              top: targetScrollTop,
              behavior: 'smooth'
            });
          }, 50);
        };

        // Execute scroll immediately
        scrollToCenter();

        // Retry scroll after a short delay to ensure it worked
        setTimeout(scrollToCenter, 200);
        setTimeout(scrollToCenter, 500);

        // Remove highlight after 2 seconds
        setTimeout(() => {
          usernameInput.classList.remove('onboarding-highlight');
        }, 2000);

        // Step 2: After username highlight, highlight buy button
        setTimeout(() => {
          const buyButton = document.getElementById('buy-button');
          if (buyButton) {
            // Add highlight class
            buyButton.classList.add('onboarding-highlight');
            buyButton.focus();

            // Force immediate scroll to center with multiple attempts
            const scrollButtonToCenter = () => {
              const rect = buyButton.getBoundingClientRect();
              const viewportHeight = window.innerHeight;
              const elementTop = rect.top;
              const elementHeight = rect.height;
              const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

              // Calculate exact center position
              const targetScrollTop = currentScrollTop + elementTop - (viewportHeight / 2) + (elementHeight / 2);

              // Force scroll immediately
              window.scrollTo({
                top: targetScrollTop,
                behavior: 'auto'
              });

              // Then smooth scroll for better UX
              setTimeout(() => {
                window.scrollTo({
                  top: targetScrollTop,
                  behavior: 'smooth'
                });
              }, 50);
            };

            // Execute scroll immediately
            scrollButtonToCenter();

            // Retry scroll after a short delay to ensure it worked
            setTimeout(scrollButtonToCenter, 200);
            setTimeout(scrollButtonToCenter, 500);

            // Remove highlight after 3 seconds
            setTimeout(() => {
              buyButton.classList.remove('onboarding-highlight');
            }, 3000);
          }
        }, 2000); // Wait 2 seconds before highlighting buy button
      }
    }, 500); // Wait 500ms before starting onboarding sequence
  };

  const formatTimeRemaining = (milliseconds: number): string => {
    const hours = Math.floor(milliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((milliseconds % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((milliseconds % (1000 * 60)) / 1000);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${seconds}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-50';
      case 'active': return 'text-green-600 bg-green-50';
      case 'sold-out': return 'text-red-600 bg-red-50';
      case 'ended': return 'text-gray-600 bg-gray-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'upcoming': return 'Coming Soon';
      case 'active': return 'Flash Sale Live!';
      case 'sold-out': return 'Sold Out';
      case 'ended': return 'Sale Ended';
      default: return status;
    }
  };

  return (
    <>
      {/* Onboarding Highlight Styles */}
      <style>
        {`
          .onboarding-highlight {
            animation: onboarding-pulse 1s ease-in-out infinite alternate;
            box-shadow: 0 0 20px rgba(59, 130, 246, 0.8), 0 0 40px rgba(59, 130, 246, 0.4);
            border: 3px solid #3b82f6 !important;
            transform: scale(1.05);
            transition: all 0.3s ease;
            z-index: 50;
            position: relative;
          }

          @keyframes onboarding-pulse {
            from {
              box-shadow: 0 0 20px rgba(59, 130, 246, 0.8), 0 0 40px rgba(59, 130, 246, 0.4);
            }
            to {
              box-shadow: 0 0 30px rgba(59, 130, 246, 1), 0 0 60px rgba(59, 130, 246, 0.6);
            }
          }

          .onboarding-highlight:focus {
            outline: none;
            box-shadow: 0 0 30px rgba(59, 130, 246, 1), 0 0 60px rgba(59, 130, 246, 0.6);
          }

          @keyframes shake {
            0%, 100% { transform: translateX(0) rotate(0deg); }
            10% { transform: translateX(-1px) rotate(-1deg); }
            20% { transform: translateX(1px) rotate(1deg); }
            30% { transform: translateX(-1px) rotate(-1deg); }
            40% { transform: translateX(1px) rotate(1deg); }
            50% { transform: translateX(-1px) rotate(-1deg); }
            60% { transform: translateX(1px) rotate(1deg); }
            70% { transform: translateX(-1px) rotate(-1deg); }
            80% { transform: translateX(1px) rotate(1deg); }
            90% { transform: translateX(-1px) rotate(-1deg); }
          }

          .notification-shake {
            animation: shake 1.2s ease-in-out infinite;
          }

          @keyframes glow {
            0%, 100% { box-shadow: 0 0 5px rgba(239, 68, 68, 0.5), 0 0 10px rgba(239, 68, 68, 0.3); }
            50% { box-shadow: 0 0 20px rgba(239, 68, 68, 0.8), 0 0 30px rgba(239, 68, 68, 0.5); }
          }

          .notification-glow {
            animation: glow 2s ease-in-out infinite;
          }
        `}
      </style>

      {/* Highlighted Onboarding Overlay */}
      {highlightedElement && (
        <div className="fixed inset-0 z-40 pointer-events-none">
          {/* Grey out everything except highlighted element */}
          <div className="absolute inset-0 bg-black bg-opacity-60"></div>

          {/* Highlight specific element */}
          {highlightedElement === 'users-tab' && (
            <div className="fixed inset-0 z-40 flex items-center justify-center bg-black bg-opacity-50">
              <div className="bg-white p-6 rounded-lg shadow-2xl max-w-md mx-4 pointer-events-auto transform -translate-y-1/2">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Click the "List of Users" Tab</h3>
                <p className="text-gray-600 text-sm mb-4">
                  This tab contains all the testing tools you need to test the flash sale system.
                </p>
                <button
                  onClick={() => {
                    setHighlightedElement('seed-users-button');
                    setActiveTab('users');
                  }}
                  className="w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Next: Seed Users
                </button>
              </div>
            </div>
          )}

          {highlightedElement === 'seed-users-button' && (
            <div className="fixed inset-0 z-40 flex items-center justify-center bg-black bg-opacity-50">
              <div className="bg-white p-6 rounded-lg shadow-2xl max-w-md mx-4 pointer-events-auto transform -translate-y-1/2">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Click "Seed Dummy Users"</h3>
                <p className="text-gray-600 text-sm mb-4">
                  This will create 10 test users that you can use to test the purchase system.
                </p>
                <button
                  onClick={() => setHighlightedElement('try-this-user-button')}
                  className="w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Next: Try This User
                </button>
              </div>
            </div>
          )}

