import React, { useState, useEffect } from 'react';
import './App.css';
import { FlashSaleStatus, PurchaseResult } from './types';
import { FlashSaleAPI } from './services/api';

function App() {
  const [status, setStatus] = useState<FlashSaleStatus | null>(null);
  const [userId, setUserId] = useState('');
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<{ hasPurchased: boolean; purchaseId?: string } | null>(null);

  // Product details for the flash sale
  const productDetails = {
    name: "iPhone 15 Pro Max - Limited Edition",
    description: "The most advanced iPhone ever with A17 Pro chip, titanium design, and revolutionary camera system. This limited edition comes with exclusive color options and premium packaging.",
    originalPrice: 1299,
    flashSalePrice: 999,
    savings: 300,
    features: [
      "A17 Pro chip with 6-core GPU",
      "Titanium design with Ceramic Shield",
      "48MP Main camera with 2x Telephoto",
      "Action Button for quick access",
      "USB-C connector",
      "iOS 17 with advanced privacy features"
    ],
    colors: ["Natural Titanium", "Blue Titanium", "White Titanium", "Black Titanium"],
    images: [
      "https://images.unsplash.com/photo-1592750475338-74b7b21085ab?w=800&h=600&fit=crop&crop=center",
      "https://images.unsplash.com/photo-1592750475338-74b7b21085ab?w=800&h=600&fit=crop&crop=center",
      "https://images.unsplash.com/photo-1592750475338-74b7b21085ab?w=800&h=600&fit=crop&crop=center"
    ],
    specifications: {
      "Display": "6.7-inch Super Retina XDR OLED",
      "Processor": "A17 Pro chip with 6-core GPU",
      "Storage": "256GB, 512GB, 1TB",
      "Camera": "48MP Main + 12MP Ultra Wide + 12MP Telephoto",
      "Battery": "Up to 29 hours video playback",
      "Water Resistance": "IP68 (maximum depth of 6 meters up to 30 minutes)"
    }
  };

  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchStatus = async () => {
    try {
      const statusData = await FlashSaleAPI.getStatus();
      setStatus(statusData);
    } catch (error) {
      console.error('Failed to fetch status:', error);
    }
  };

  const handlePurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    setLoading(true);
    try {
      const result = await FlashSaleAPI.attemptPurchase(userId);
      setPurchaseResult(result);

      if (result.success) {
        setUserPurchaseStatus({ hasPurchased: true, purchaseId: result.purchaseId });
      }

      fetchStatus();
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'An error occurred during purchase',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const checkUserPurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    try {
      const result = await FlashSaleAPI.checkUserPurchase(userId);
      setUserPurchaseStatus(result);
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const formatTime = (milliseconds: number | undefined) => {
    if (!milliseconds) return '';

    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m ${seconds % 60}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-50 border-blue-200';
      case 'active': return 'text-green-600 bg-green-50 border-green-200';
      case 'sold-out': return 'text-red-600 bg-red-50 border-red-200';
      case 'ended': return 'text-gray-600 bg-gray-50 border-gray-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'upcoming': return '‚è∞';
      case 'active': return 'üî•';
      case 'sold-out': return '‚ùå';
      case 'ended': return 'üèÅ';
      default: return '‚ùì';
    }
  };

  const getStatusGradient = (status: string) => {
    switch (status) {
      case 'upcoming': return 'from-blue-500 to-blue-600';
      case 'active': return 'from-green-500 to-green-600';
      case 'sold-out': return 'from-red-500 to-red-600';
      case 'ended': return 'from-gray-500 to-gray-600';
      default: return 'from-gray-500 to-gray-600';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Header */}
      <div className="bg-white/80 backdrop-blur-sm border-b border-gray-200 sticky top-0 z-50">
        <div className="container mx-auto px-6 py-4">
          <div className="text-center">
            <h1 className="text-4xl md:text-5xl font-bold bg-gradient-to-r from-purple-600 via-blue-600 to-indigo-600 bg-clip-text text-transparent">
              ‚ö° Flash Sale System
            </h1>
            <p className="text-gray-600 text-lg mt-2 font-medium">
              High-Throughput Limited Edition Product Sale
            </p>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-6 py-8">
        {/* Product Showcase */}
        <div className="bg-white/70 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-8 mb-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Product Images */}
            <div className="space-y-4">
              <div className="relative group">
                <img 
                  src={productDetails.images[0]} 
                  alt={productDetails.name}
                  className="w-full h-96 object-cover rounded-xl shadow-lg group-hover:scale-105 transition-transform duration-300"
                />
                <div className="absolute top-4 left-4">
                  <span className="bg-red-500 text-white px-3 py-1 rounded-full text-sm font-bold animate-pulse">
                    üî• FLASH SALE
                  </span>
                </div>
              </div>
              <div className="flex space-x-2">
                {productDetails.images.slice(1).map((image, index) => (
                  <img 
                    key={index}
                    src={image} 
                    alt={`${productDetails.name} view ${index + 2}`}
                    className="w-20 h-20 object-cover rounded-lg cursor-pointer hover:scale-110 transition-transform duration-200"
                  />
                ))}
              </div>
            </div>

            {/* Product Details */}
            <div className="space-y-6">
              <div>
                <h2 className="text-3xl font-bold text-gray-800 mb-2">{productDetails.name}</h2>
                <p className="text-gray-600 text-lg leading-relaxed">{productDetails.description}</p>
              </div>

              {/* Pricing */}
              <div className="bg-gradient-to-r from-red-50 to-pink-50 p-6 rounded-xl border-2 border-red-200">
                <div className="flex items-baseline space-x-4 mb-2">
                  <span className="text-4xl font-bold text-red-600">${productDetails.flashSalePrice}</span>
                  <span className="text-xl text-gray-500 line-through">${productDetails.originalPrice}</span>
                  <span className="bg-red-500 text-white px-3 py-1 rounded-full text-sm font-bold">
                    Save ${productDetails.savings}
                  </span>
                </div>
                <p className="text-red-700 font-semibold">Limited Time Offer - While Supplies Last!</p>
              </div>

              {/* Features */}
              <div>
                <h3 className="text-xl font-semibold text-gray-800 mb-3">‚ú® Key Features</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                  {productDetails.features.map((feature, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <span className="text-green-500">‚úì</span>
                      <span className="text-gray-700">{feature}</span>
                    </div>
                  ))}
                </div>
              </div>

              {/* Colors */}
              <div>
                <h3 className="text-xl font-semibold text-gray-800 mb-3">üé® Available Colors</h3>
                <div className="flex flex-wrap gap-2">
                  {productDetails.colors.map((color, index) => (
                    <span 
                      key={index}
                      className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm border border-gray-300"
                    >
                      {color}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Specifications */}
          <div className="mt-8 pt-8 border-t border-gray-200">
            <h3 className="text-2xl font-bold text-gray-800 mb-6 text-center">üìã Technical Specifications</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {Object.entries(productDetails.specifications).map(([key, value]) => (
                <div key={key} className="bg-gray-50 p-4 rounded-lg">
                  <dt className="font-semibold text-gray-800 mb-1">{key}</dt>
                  <dd className="text-gray-600 text-sm">{value}</dd>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Status Card */}
        {status && (
          <div className="bg-white/70 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-8 mb-8 transform transition-all duration-300 hover:scale-[1.02] hover:shadow-2xl" data-testid="flash-sale-status">
            <div className="text-center mb-8">
              <div className="text-8xl mb-6 animate-pulse">{getStatusIcon(status.status)}</div>
              <div className={`inline-flex items-center px-6 py-3 rounded-full border-2 ${getStatusColor(status.status)}`}>
                <h2 className={`text-3xl font-bold ${getStatusColor(status.status).split(' ')[0]} mb-0`} data-testid="status-text">
                  {status.status.charAt(0).toUpperCase() + status.status.slice(1)}
                </h2>
              </div>
              <div className="text-gray-600 mt-4 text-lg font-medium" data-testid="time-info">
                {status.status === 'upcoming' && status.timeUntilStart && (
                  <div className="bg-gradient-to-r from-blue-500 to-purple-600 text-white px-6 py-3 rounded-full inline-block">
                    ‚è±Ô∏è Starts in: {formatTime(status.timeUntilStart)}
                  </div>
                )}
                {status.status === 'active' && status.timeUntilEnd && (
                  <div className="bg-gradient-to-r from-green-500 to-emerald-600 text-white px-6 py-3 rounded-full inline-block">
                    ‚è∞ Ends in: {formatTime(status.timeUntilEnd)}
                  </div>
                )}
              </div>
            </div>

            {/* Stock Information */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8" data-testid="stock-info">
              <div className="text-center group">
                <div className="bg-gradient-to-br from-blue-500 to-blue-600 text-white p-6 rounded-2xl transform transition-all duration-300 group-hover:scale-105 group-hover:shadow-lg">
                  <div className="text-3xl font-bold" data-testid="total-stock">{status.maxStock}</div>
                  <div className="text-blue-100 font-medium">Total Stock</div>
                </div>
              </div>
              <div className="text-center group">
                <div className="bg-gradient-to-br from-green-500 to-green-600 text-white p-6 rounded-2xl transform transition-all duration-300 group-hover:scale-105 group-hover:shadow-lg">
                  <div className="text-3xl font-bold" data-testid="current-stock">{status.currentStock}</div>
                  <div className="text-green-100 font-medium">Available</div>
                </div>
              </div>
              <div className="text-center group">
                <div className="bg-gradient-to-br from-purple-500 to-purple-600 text-white p-6 rounded-2xl transform transition-all duration-300 group-hover:scale-105 group-hover:shadow-lg">
                  <div className="text-3xl font-bold" data-testid="sold-stock">{status.maxStock - status.currentStock}</div>
                  <div className="text-purple-100 font-medium">Sold</div>
                </div>
              </div>
            </div>

            {/* Progress Bar */}
            <div className="mb-6">
              <div className="flex justify-between text-sm text-gray-600 mb-2">
                <span>Progress</span>
                <span>{Math.round(((status.maxStock - status.currentStock) / status.maxStock) * 100)}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-3">
                <div 
                  className={`h-3 rounded-full transition-all duration-500 ${getStatusGradient(status.status)}`}
                  style={{ width: `${((status.maxStock - status.currentStock) / status.maxStock) * 100}%` }}
                ></div>
              </div>
            </div>

            {/* Time Information */}
            <div className="text-center text-sm text-gray-500 bg-gray-50 rounded-xl p-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <p className="font-medium text-gray-700">Start Time</p>
                  <p>{new Date(status.startTime).toLocaleString()}</p>
                </div>
                <div>
                  <p className="font-medium text-gray-700">End Time</p>
                  <p>{new Date(status.endTime).toLocaleString()}</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Purchase Section */}
        <div className="bg-white/70 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-8 mb-8" data-testid="purchase-section">
          <h3 className="text-2xl font-bold mb-6 text-gray-800 flex items-center">
            üõí Purchase Section
          </h3>

          <div className="mb-6">
            <label htmlFor="userId" className="block text-sm font-semibold text-gray-700 mb-3">
              User ID
            </label>
            <input
              type="text"
              id="userId"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              placeholder="Enter your user ID (e.g., email or username)"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:ring-4 focus:ring-blue-500/20 focus:border-blue-500 transition-all duration-200 text-lg"
              data-testid="user-id-input"
            />
          </div>

          <div className="flex flex-wrap gap-4 mb-6">
            <button
              onClick={handlePurchase}
              disabled={loading || status?.status !== 'active'}
              className={`px-8 py-3 rounded-xl font-semibold text-lg transition-all duration-200 transform hover:scale-105 focus:ring-4 focus:ring-red-500/20 ${
                loading || status?.status !== 'active'
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-gradient-to-r from-red-500 to-red-600 text-white hover:from-red-600 hover:to-red-700 shadow-lg hover:shadow-xl'
              }`}
              data-testid="buy-now-button"
            >
              {loading ? (
                <div className="flex items-center">
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                  Processing...
                </div>
              ) : (
                'üöÄ Buy Now!'
              )}
            </button>

            <button
              onClick={checkUserPurchase}
              disabled={!userId.trim()}
              className={`px-8 py-3 rounded-xl font-semibold text-lg transition-all duration-200 transform hover:scale-105 focus:ring-4 focus:ring-blue-500/20 ${
                !userId.trim()
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-gradient-to-r from-blue-500 to-blue-600 text-white hover:from-blue-600 hover:to-blue-700 shadow-lg hover:shadow-xl'
              }`}
              data-testid="check-purchase-button"
            >
              üîç Check Purchase Status
            </button>
          </div>

          {/* Purchase Result */}
          {purchaseResult && (
            <div className={`${
              purchaseResult.success 
                ? 'bg-gradient-to-r from-green-50 to-emerald-50 border-2 border-green-200 text-green-800' 
                : 'bg-gradient-to-r from-red-50 to-pink-50 border-2 border-red-200 text-red-800'
            } p-6 rounded-xl shadow-lg`}
              data-testid="purchase-result"
            >
              <div className="font-bold text-lg mb-2">{purchaseResult.message}</div>
              {purchaseResult.purchaseId && (
                <div className="text-sm bg-white/50 px-3 py-2 rounded-lg inline-block">
                  üÜî Purchase ID: {purchaseResult.purchaseId}
                </div>
              )}
              <div className="text-sm mt-3 opacity-75">
                ‚è∞ {new Date(purchaseResult.timestamp).toLocaleString()}
              </div>
            </div>
          )}

          {/* User Purchase Status */}
          {userPurchaseStatus && (
            <div className={`${
              userPurchaseStatus.hasPurchased 
                ? 'bg-gradient-to-r from-blue-50 to-indigo-50 border-2 border-blue-200 text-blue-800' 
                : 'bg-gradient-to-r from-gray-50 to-slate-50 border-2 border-gray-200 text-gray-800'
            } p-6 rounded-xl shadow-lg mt-4`}
              data-testid="user-purchase-status"
            >
              <div className="font-bold text-lg mb-2">
                {userPurchaseStatus.hasPurchased 
                  ? `üéâ Congratulations! You have successfully purchased an item!` 
                  : `‚ÑπÔ∏è No purchase found for user: ${userId}`
                }
              </div>
              {userPurchaseStatus.purchaseId && (
                <div className="text-sm bg-white/50 px-3 py-2 rounded-lg inline-block">
                  üÜî Purchase ID: {userPurchaseStatus.purchaseId}
                </div>
              )}
            </div>
          )}
        </div>

        {/* System Information */}
        <div className="bg-white/70 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-8">
          <h3 className="text-2xl font-bold mb-6 text-gray-800 flex items-center">
            ‚öôÔ∏è System Information
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-gradient-to-br from-blue-50 to-indigo-50 p-6 rounded-xl border border-blue-200">
              <h4 className="font-semibold text-blue-800 mb-3">üñ•Ô∏è Backend Technology</h4>
              <div className="space-y-2 text-sm text-blue-700">
                <p><strong>Framework:</strong> Node.js + NestJS + Express</p>
                <p><strong>Database:</strong> Redis + PostgreSQL</p>
                <p><strong>Message Queue:</strong> Kafka (Redpanda)</p>
              </div>
            </div>
            <div className="bg-gradient-to-br from-green-50 to-emerald-50 p-6 rounded-xl border border-green-200">
              <h4 className="font-semibold text-green-800 mb-3">üé® Frontend Technology</h4>
              <div className="space-y-2 text-sm text-green-700">
                <p><strong>Framework:</strong> React + TypeScript</p>
                <p><strong>Styling:</strong> Tailwind CSS</p>
                <p><strong>State Management:</strong> React Hooks</p>
              </div>
            </div>
          </div>
          <div className="mt-6 bg-gradient-to-br from-purple-50 to-pink-50 p-6 rounded-xl border border-purple-200">
            <h4 className="font-semibold text-purple-800 mb-3">üöÄ System Features</h4>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-purple-700">
              <div className="flex items-center">
                <span className="mr-2">‚ö°</span>
                <span>High-throughput</span>
              </div>
              <div className="flex items-center">
                <span className="mr-2">üîÑ</span>
                <span>Concurrency Control</span>
              </div>
              <div className="flex items-center">
                <span className="mr-2">üõ°Ô∏è</span>
                <span>Fault-tolerant</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
