                {/* Stock Progress */}
                <div className="mt-4">
                  <div className="flex justify-between text-sm text-gray-600 mb-2">
                    <span>Progress Penjualan</span>
                    <span>{Math.round(((status.maxStock - status.currentStock) / status.maxStock) * 100)}%</span>
