import React, { useState, useEffect } from 'react';
import { FlashSaleStatus, PurchaseResult } from './types';

const App: React.FC = () => {
  const [flashSaleStatus, setFlashSaleStatus] = useState<FlashSaleStatus | null>(null);
  const [userId, setUserId] = useState('');
  const [loading, setLoading] = useState(false);
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<{ hasPurchased: boolean; purchaseId?: string } | null>(null);
  const [activeTab, setActiveTab] = useState<'detail' | 'specifications' | 'important'>('detail');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Sample product images for the slider
  const productImages = [
    'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=500&h=500&fit=crop'
  ];

  useEffect(() => {
    fetchFlashSaleStatus();
    const interval = setInterval(fetchFlashSaleStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchFlashSaleStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/status');
      if (response.ok) {
        const data = await response.json();
        setFlashSaleStatus(data);
      }
    } catch (error) {
      console.error('Failed to fetch flash sale status:', error);
    }
  };

  const attemptPurchase = async () => {
    if (!userId.trim()) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId: userId.trim() }),
      });

      if (response.ok) {
        const result = await response.json();
        setPurchaseResult(result);
        if (result.success) {
          fetchFlashSaleStatus();
        }
      } else {
        const errorData = await response.json();
        setPurchaseResult(errorData);
      }
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'Network error. Please try again.',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const checkUserPurchase = async () => {
    if (!userId.trim()) return;

    try {
      const response = await fetch(`http://localhost:3001/api/flash-sale/purchase/${userId.trim()}`);
      if (response.ok) {
        const data = await response.json();
        setUserPurchaseStatus(data);
      }
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const formatTimeRemaining = (milliseconds: number): string => {
    const hours = Math.floor(milliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((milliseconds % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((milliseconds % (1000 * 60)) / 1000);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${seconds}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-50';
      case 'active': return 'text-green-600 bg-green-50';
      case 'sold-out': return 'text-red-600 bg-red-50';
      case 'ended': return 'text-gray-600 bg-gray-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'upcoming': return 'üöÄ Coming Soon';
      case 'active': return 'üî• FLASH SALE LIVE!';
      case 'sold-out': return '‚ùå Sold Out';
      case 'ended': return '‚è∞ Sale Ended';
      default: return status;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">
            üöÄ Flash Sale System
          </h1>
          <p className="text-lg text-gray-600">
            Limited Edition Premium Wireless Earbuds
          </p>
        </div>

        {/* Main Content - 3 Column Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">

          {/* Left Column - Image Slider */}
          <div className="lg:col-span-5">
            <div className="bg-white rounded-2xl shadow-lg p-6">
              {/* Main Image */}
              <div className="relative mb-4">
                <img
                  src={productImages[currentImageIndex]}
                  alt="Premium Wireless Earbuds"
                  className="w-full h-96 object-cover rounded-xl"
                />
                {/* Navigation Arrows */}
                <button
                  onClick={() => setCurrentImageIndex(prev => prev === 0 ? productImages.length - 1 : prev - 1)}
                  className="absolute left-3 top-1/2 transform -translate-y-1/2 bg-white/80 hover:bg-white text-gray-800 p-2 rounded-full shadow-lg transition-all"
                >
                  ‚Üê
                </button>
                <button
                  onClick={() => setCurrentImageIndex(prev => prev === productImages.length - 1 ? 0 : prev + 1)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 bg-white/80 hover:bg-white text-gray-800 p-2 rounded-full shadow-lg transition-all"
                >
                  ‚Üí
                </button>
              </div>

              {/* Thumbnails */}
              <div className="flex space-x-2 justify-center">
                {productImages.map((image, index) => (
                  <button
                    key={index}
                    onClick={() => setCurrentImageIndex(index)}
                    className={`w-16 h-16 rounded-lg overflow-hidden border-2 transition-all ${
                      currentImageIndex === index 
                        ? 'border-blue-500 shadow-lg' 
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <img
                      src={image}
                      alt={`Product view ${index + 1}`}
                      className="w-full h-full object-cover"
                    />
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Center Column - Product Details */}
          <div className="lg:col-span-4">
            <div className="bg-white rounded-2xl shadow-lg p-6">
              {/* Product Title */}
              <h2 className="text-2xl font-bold text-gray-900 mb-2">
                Premium Wireless Earbuds Pro
              </h2>

              {/* Rating */}
              <div className="flex items-center mb-4">
                <div className="flex text-yellow-400">
                  {[...Array(5)].map((_, i) => (
                    <span key={i}>‚≠ê</span>
                  ))}
                </div>
                <span className="ml-2 text-gray-600">4.9 (128 reviews)</span>
              </div>

              {/* Flash Sale Status */}
              {flashSaleStatus && (
                <div className={`mb-6 p-4 rounded-lg ${getStatusColor(flashSaleStatus.status)}`}>
                  <h3 className="font-semibold text-lg mb-2">
                    {getStatusText(flashSaleStatus.status)}
                  </h3>

                  {flashSaleStatus.status === 'upcoming' && flashSaleStatus.timeUntilStart && (
                    <div className="text-center p-3 bg-blue-100 rounded-lg mb-4">
                      <p className="text-sm text-blue-800 mb-1">Sale starts in:</p>
                      <p className="text-xl font-bold text-blue-900">
                        {formatTimeRemaining(flashSaleStatus.timeUntilStart)}
                      </p>
                    </div>
                  )}

                  {flashSaleStatus.status === 'active' && flashSaleStatus.timeUntilEnd && (
                    <div className="text-center p-3 bg-green-100 rounded-lg mb-4">
                      <p className="text-sm text-green-800 mb-1">Sale ends in:</p>
                      <p className="text-xl font-bold text-green-900">
                        {formatTimeRemaining(flashSaleStatus.timeUntilEnd)}
                      </p>
                    </div>
                  )}

                  {/* Stock Information */}
                  <div className="text-center">
                    <p className="text-sm text-gray-600 mb-1">Available Stock:</p>
                    <p className="text-2xl font-bold text-gray-900">
                      {flashSaleStatus.currentStock} / {flashSaleStatus.maxStock}
                    </p>
                  </div>
                </div>
              )}

              {/* Product Tabs */}
              <div className="mb-6">
                <div className="flex border-b border-gray-200">
                  {[
                    { id: 'detail', label: 'Detail' },
                    { id: 'specifications', label: 'Specifications' },
                    { id: 'important', label: 'Important Info' }
                  ].map((tab) => (
                    <button
                      key={tab.id}
                      onClick={() => setActiveTab(tab.id as any)}
                      className={`px-4 py-2 text-sm font-medium border-b-2 transition-colors ${
                        activeTab === tab.id
                          ? 'border-blue-500 text-blue-600'
                          : 'border-transparent text-gray-500 hover:text-gray-700'
                      }`}
                    >
                      {tab.label}
                    </button>
                  ))}
                </div>

                {/* Tab Content */}
                <div className="mt-4">
                  {activeTab === 'detail' && (
                    <div className="space-y-3">
                      <div className="flex justify-between">
                        <span className="text-gray-600">Condition:</span>
                        <span className="font-medium">Brand New</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Warranty:</span>
                        <span className="font-medium">2 Years</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Weight:</span>
                        <span className="font-medium">250g</span>
                      </div>
                      <div className="pt-3">
                        <p className="text-gray-700 leading-relaxed">
                          Experience crystal-clear sound with our premium wireless earbuds. 
                          Features include active noise cancellation, 30-hour battery life, 
                          and premium build quality. Perfect for music lovers and professionals.
                        </p>
                      </div>
                    </div>
                  )}

                  {activeTab === 'specifications' && (
                    <div className="space-y-3">
                      <div className="flex justify-between">
                        <span className="text-gray-600">Bluetooth:</span>
                        <span className="font-medium">5.2</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Battery Life:</span>
                        <span className="font-medium">30 hours</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Water Resistance:</span>
                        <span className="font-medium">IPX4</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Driver Size:</span>
                        <span className="font-medium">10mm</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Frequency Response:</span>
                        <span className="font-medium">20Hz-20kHz</span>
                      </div>
                    </div>
                  )}

                  {activeTab === 'important' && (
                    <div className="space-y-3">
                      <div className="p-3 bg-yellow-50 rounded-lg">
                        <p className="text-sm text-yellow-800">
                          ‚ö†Ô∏è This is a limited edition product. Only 100 units available worldwide.
                        </p>
                      </div>
                      <div className="p-3 bg-blue-50 rounded-lg">
                        <p className="text-sm text-blue-800">
                          üì± Compatible with all devices supporting Bluetooth 5.0+
                        </p>
                      </div>
                      <div className="p-3 bg-green-50 rounded-lg">
                        <p className="text-sm text-green-800">
                          üöö Free worldwide shipping included
                        </p>
                      </div>
                      <div className="p-3 bg-purple-50 rounded-lg">
                        <p className="text-sm text-purple-800">
                          üí≥ Secure payment processing guaranteed
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Right Column - Floating Purchase Section */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-2xl shadow-lg p-6 sticky top-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">
                Purchase Section
              </h3>

              {/* User ID Input */}
              <div className="mb-4">
                <label htmlFor="userId" className="block text-sm font-medium text-gray-700 mb-2">
                  User ID
                </label>
                <input
                  type="text"
                  id="userId"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  placeholder="Enter your user ID"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              {/* Check Purchase Button */}
              <button
                onClick={checkUserPurchase}
                disabled={!userId.trim()}
                className="w-full mb-4 px-4 py-2 bg-gray-100 text-gray-700 rounded-lg font-medium hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                Check Purchase Status
              </button>

              {/* Purchase Status Display */}
              {userPurchaseStatus && (
                <div className="mb-4 p-3 rounded-lg bg-gray-50">
                  <p className="text-sm text-gray-700">
                    {userPurchaseStatus.hasPurchased ? (
                      <span className="text-green-600 font-medium">
                        ‚úÖ You have already purchased this item!
                        {userPurchaseStatus.purchaseId && (
                          <>
                            <br />
                            Purchase ID: {userPurchaseStatus.purchaseId}
                          </>
                        )}
                      </span>
                    ) : (
                      <span className="text-blue-600 font-medium">
                        üÜï You haven't purchased this item yet
                      </span>
                    )}
                  </p>
                </div>
              )}

              {/* Buy Now Button */}
              <button
                onClick={attemptPurchase}
                disabled={!userId.trim() || loading || flashSaleStatus?.status !== 'active' || userPurchaseStatus?.hasPurchased}
                className="w-full mb-4 px-6 py-3 bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white font-bold text-lg rounded-lg shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 transition-all disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    Processing...
                  </div>
                ) : (
                  'üî• BUY NOW!'
                )}
              </button>

              {/* Purchase Result */}
              {purchaseResult && (
                <div className={`p-3 rounded-lg ${
                  purchaseResult.success ? 'bg-green-50' : 'bg-red-50'
                }`}>
                  <p className={`text-sm font-medium ${
                    purchaseResult.success ? 'text-green-800' : 'text-red-800'
                  }`}>
                    {purchaseResult.success ? 'üéâ ' : '‚ùå '}
                    {purchaseResult.message}
                    {purchaseResult.success && purchaseResult.purchaseId && (
                      <>
                        <br />
                        Purchase ID: {purchaseResult.purchaseId}
                      </>
                    )}
                  </p>
                </div>
              )}

              {/* Price Display */}
              <div className="text-center pt-4 border-t border-gray-200">
                <div className="mb-2">
                  <span className="text-3xl font-bold text-gray-900">$299</span>
                  <span className="text-lg text-gray-500 line-through ml-2">$499</span>
                </div>
                <p className="text-xs text-gray-500 mt-1">Free shipping included</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
