import React, { useState, useEffect } from 'react';
import { FlashSaleStatus, PurchaseResult, User, UserSeedResponse, Product } from './types';
import { 
  FlashSaleIcon, 
  ClockIcon, 
  CheckIcon, 
  InfoIcon, 
  WarningIcon, 
  ShoppingBagIcon, 
  StarIcon, 
  ArrowLeftIcon, 
  ArrowRightIcon 
} from './components/icons';

const App: React.FC = () => {
  const [flashSaleStatus, setFlashSaleStatus] = useState<FlashSaleStatus | null>(null);
  const [username, setUsername] = useState('john_doe');
  const [loading, setLoading] = useState(false);
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<{ hasPurchased: boolean; purchaseId?: string } | null>(null);
  const [activeTab, setActiveTab] = useState<'detail' | 'specifications' | 'important' | 'users'>('detail');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [users, setUsers] = useState<User[]>([]);
  const [usersLoading, setUsersLoading] = useState(false);
  const [product, setProduct] = useState<Product | null>(null);
  const [productLoading, setProductLoading] = useState(true);
  const [showOnboarding, setShowOnboarding] = useState(true);
  const [onboardingStep, setOnboardingStep] = useState(0);

  useEffect(() => {
    fetchFlashSaleStatus();
    fetchProduct();
    const interval = setInterval(fetchFlashSaleStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (activeTab === 'users') {
      fetchUsers();
    }
  }, [activeTab]);

  const fetchFlashSaleStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/status');
      if (response.ok) {
        const data = await response.json();
        setFlashSaleStatus(data);
      }
    } catch (error) {
      console.error('Failed to fetch flash sale status:', error);
    }
  };

  const fetchProduct = async () => {
    try {
      setProductLoading(true);
      const response = await fetch('http://localhost:3001/api/flash-sale/product');
      if (response.ok) {
        const data = await response.json();
        setProduct(data);
      }
    } catch (error) {
      console.error('Failed to fetch product data:', error);
    } finally {
      setProductLoading(false);
    }
  };

  const attemptPurchase = async () => {
    if (!username.trim()) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userIdentifier: username.trim() }),
      });

      if (response.ok) {
        const result = await response.json();
        setPurchaseResult(result);
        if (result.success) {
          fetchFlashSaleStatus();
          fetchUsers(); // Refresh user list to update purchase status
        }
      } else {
        const errorData = await response.json();
        setPurchaseResult(errorData);
      }
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'Network error. Please try again.',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const checkUserPurchase = async () => {
    if (!username.trim()) return;

    try {
      const response = await fetch(`http://localhost:3001/api/flash-sale/purchase/${username.trim()}`);
      if (response.ok) {
        const data = await response.json();
        setUserPurchaseStatus(data);
      }
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const fetchUsers = async () => {
    setUsersLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/users');
      if (response.ok) {
        const data = await response.json();
        setUsers(data);
      }
    } catch (error) {
      console.error('Failed to fetch users:', error);
    } finally {
      setUsersLoading(false);
    }
  };

  const seedUsers = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/users/seed', {
        method: 'POST',
      });
      if (response.ok) {
        const data: UserSeedResponse = await response.json();
        console.log(data.message);
        fetchUsers(); // Refresh the users list
      }
    } catch (error) {
      console.error('Failed to seed users:', error);
    }
  };

  const resetUsers = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/users/reset', {
        method: 'DELETE',
      });
      if (response.ok) {
        setUsers([]);
        console.log('Users reset successfully');
      }
    } catch (error) {
      console.error('Failed to reset users:', error);
    }
  };

  const formatTimeRemaining = (milliseconds: number): string => {
    const hours = Math.floor(milliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((milliseconds % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((milliseconds % (1000 * 60)) / 1000);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${seconds}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-50';
      case 'active': return 'text-green-600 bg-green-50';
      case 'sold-out': return 'text-red-600 bg-red-50';
      case 'ended': return 'text-gray-600 bg-gray-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'upcoming': return 'Coming Soon';
      case 'active': return 'Flash Sale Live!';
      case 'sold-out': return 'Sold Out';
      case 'ended': return 'Sale Ended';
      default: return status;
    }
  };

  return (
    <>
      {/* Onboarding Tutorial Modal */}
      {showOnboarding && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            {/* Header */}
            <div className="p-6 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h2 className="text-2xl font-bold text-gray-900">Flash Sale Testing Guide</h2>
                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => setShowOnboarding(false)}
                    className="px-3 py-1 text-sm text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors"
                  >
                    Skip Guide
                  </button>
                  <button
                    onClick={() => setShowOnboarding(false)}
                    className="text-gray-400 hover:text-gray-600 transition-colors"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
              </div>
              <p className="text-gray-600 mt-2">Follow this guide to test each tab and feature of the application</p>
            </div>

            {/* Tutorial Content */}
            <div className="p-6">
              {onboardingStep === 0 && (
                <div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-4">Application Overview</h3>
                  <p className="text-gray-700 mb-6">
                    This Flash Sale System has <strong>4 main tabs</strong> that you can test. 
                    Each tab serves a specific purpose and contains different testing tools.
                  </p>

                  <div className="grid grid-cols-2 gap-4 mb-6">
                    <div className="bg-blue-50 p-4 rounded-lg border-l-4 border-blue-400">
                      <h4 className="font-medium text-blue-900 mb-2">Detail Tab</h4>
                      <p className="text-blue-700 text-sm">Product information, images, and purchase section</p>
                    </div>
                    <div className="bg-green-50 p-4 rounded-lg border-l-4 border-green-400">
                      <h4 className="font-medium text-green-900 mb-2">Specifications Tab</h4>
                      <p className="text-green-700 text-sm">Product specs like Bluetooth, battery life, etc.</p>
                    </div>
                    <div className="bg-yellow-50 p-4 rounded-lg border-l-4 border-yellow-400">
                      <h4 className="font-medium text-yellow-900 mb-2">Important Tab</h4>
                      <p className="text-yellow-700 text-sm">Key product information and restrictions</p>
                    </div>
                    <div className="bg-purple-50 p-4 rounded-lg border-l-4 border-purple-400">
                      <h4 className="font-medium text-purple-900 mb-2">Users Tab</h4>
                      <p className="text-purple-700 text-sm">User management and testing tools</p>
                    </div>
                  </div>

                  <div className="bg-gray-50 p-4 rounded-lg">
                    <p className="text-gray-700 text-sm">
                      <strong>Start with:</strong> Click on each tab to explore its features, then use the "Users" tab 
