import React, { useState, useEffect } from 'react';
import './App.css';

interface FlashSaleStatus {
  status: 'upcoming' | 'active' | 'ended' | 'sold-out';
  startTime: string;
  endTime: string;
  currentStock: number;
  maxStock: number;
  timeUntilStart?: number;
  timeUntilEnd?: number;
}

interface PurchaseResult {
  success: boolean;
  message: string;
  timestamp: string;
  userId: string;
  purchaseId?: string;
}

const App: React.FC = () => {
  const [status, setStatus] = useState<FlashSaleStatus | null>(null);
  const [userId, setUserId] = useState('');
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('detail');
  const [selectedImage, setSelectedImage] = useState(0);

  // Sample product images (you can replace with actual images)
  const productImages = [
    'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=500&h=500&fit=crop'
  ];

  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 5000); // Refresh every 5 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/status');
      if (response.ok) {
        const data = await response.json();
        setStatus(data);
      }
    } catch (error) {
      console.error('Failed to fetch status:', error);
    }
  };

  const handlePurchase = async () => {
    if (!userId.trim()) {
      setPurchaseResult({
        success: false,
        message: 'Please enter a user ID',
        timestamp: new Date().toISOString(),
        userId: ''
      });
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId: userId.trim() }),
      });

      const result = await response.json();
      setPurchaseResult(result);
    } catch (error) {
      setPurchaseResult({
        success: false,
        message: 'Failed to connect to server',
        timestamp: new Date().toISOString(),
        userId: userId.trim()
      });
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (milliseconds: number) => {
    const hours = Math.floor(milliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((milliseconds % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((milliseconds % (1000 * 60)) / 1000);
    return `${hours}h ${minutes}m ${seconds}s`;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-100';
      case 'active': return 'text-green-600 bg-green-100';
      case 'ended': return 'text-red-600 bg-red-100';
      case 'sold-out': return 'text-orange-600 bg-orange-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'upcoming': return 'Coming Soon';
      case 'active': return 'Live Now';
      case 'ended': return 'Sale Ended';
      case 'sold-out': return 'Sold Out';
      default: return 'Unknown';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <h1 className="text-2xl font-bold text-gray-900">Flash Sale System</h1>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

          {/* Left Column - Image Slider */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-lg p-6">
              {/* Main Image */}
              <div className="relative mb-4">
                <img
                  src={productImages[selectedImage]}
                  alt="Product"
                  className="w-full h-96 object-cover rounded-lg"
                />
                {/* Status Badge */}
                {status && (
                  <div className={`absolute top-4 left-4 px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(status.status)}`}>
                    {getStatusText(status.status)}
                  </div>
                )}
              </div>

              {/* Thumbnails */}
              <div className="grid grid-cols-4 gap-2">
                {productImages.map((image, index) => (
                  <button
                    key={index}
                    onClick={() => setSelectedImage(index)}
                    className={`relative overflow-hidden rounded-lg border-2 transition-all ${
                      selectedImage === index 
                        ? 'border-blue-500 ring-2 ring-blue-200' 
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <img
                      src={image}
                      alt={`Product ${index + 1}`}
                      className="w-full h-20 object-cover"
                    />
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Center Column - Product Details */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-lg p-6">
              {/* Product Title */}
              <h2 className="text-2xl font-bold text-gray-900 mb-2">
                Premium Wireless Headphones
              </h2>

              {/* Rating and Sales */}
              <div className="flex items-center mb-4">
                <div className="flex items-center">
                  <div className="flex text-yellow-400">
                    {[...Array(5)].map((_, i) => (
                      <svg key={i} className="w-5 h-5 fill-current" viewBox="0 0 20 20">
                        <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z"/>
                      </svg>
                    ))}
                  </div>
                  <span className="ml-2 text-sm text-gray-600">4.8 (127 reviews)</span>
                </div>
                <span className="ml-4 text-sm text-gray-500">• Sold 500+</span>
              </div>

              {/* Price */}
              <div className="mb-6">
                <span className="text-3xl font-bold text-green-600">$99.99</span>
                <span className="ml-2 text-lg text-gray-500 line-through">$149.99</span>
                <span className="ml-2 text-sm text-green-600 font-medium">33% OFF</span>
              </div>

              {/* Stock Status */}
              {status && (
                <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm font-medium text-gray-700">Available Stock</span>
                    <span className="text-sm text-gray-500">{status.currentStock} of {status.maxStock}</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-green-500 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${(status.currentStock / status.maxStock) * 100}%` }}
                    ></div>
                  </div>
                  {status.timeUntilStart && status.timeUntilStart > 0 && (
                    <p className="text-sm text-blue-600 mt-2">
                      Sale starts in: {formatTime(status.timeUntilStart)}
                    </p>
                  )}
                  {status.timeUntilEnd && status.timeUntilEnd > 0 && status.status === 'active' && (
                    <p className="text-sm text-red-600 mt-2">
                      Sale ends in: {formatTime(status.timeUntilEnd)}
                    </p>
                  )}
                </div>
              )}

              {/* Tabs */}
              <div className="border-b border-gray-200 mb-6">
                <nav className="-mb-px flex space-x-8">
                  {['detail', 'specification', 'info'].map((tab) => (
                    <button
                      key={tab}
                      onClick={() => setActiveTab(tab)}
                      className={`py-2 px-1 border-b-2 font-medium text-sm ${
                        activeTab === tab
                          ? 'border-blue-500 text-blue-600'
                          : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                      }`}
                    >
                      {tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                  ))}
                </nav>
              </div>

              {/* Tab Content */}
              <div className="min-h-[200px]">
                {activeTab === 'detail' && (
                  <div>
                    <h3 className="font-medium text-gray-900 mb-3">Product Description</h3>
                    <p className="text-gray-600 leading-relaxed">
                      Experience premium sound quality with our latest wireless headphones. 
                      Features include active noise cancellation, 30-hour battery life, 
                      premium comfort design, and seamless Bluetooth connectivity. 
                      Perfect for music lovers and professionals alike.
                    </p>
                    <div className="mt-4 space-y-2">
                      <div className="flex items-center text-sm text-gray-600">
                        <span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>
                        Active Noise Cancellation
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>
                        30-Hour Battery Life
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>
                        Premium Comfort Design
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>
                        Bluetooth 5.0
                      </div>
                    </div>
                  </div>
                )}

                {activeTab === 'specification' && (
                  <div>
                    <h3 className="font-medium text-gray-900 mb-3">Technical Specifications</h3>
                    <div className="space-y-3 text-sm text-gray-600">
                      <div className="flex justify-between">
                        <span>Driver Size:</span>
                        <span>40mm Dynamic</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Frequency Response:</span>
                        <span>20Hz - 20kHz</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Impedance:</span>
                        <span>32 Ohms</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Battery:</span>
                        <span>Lithium-ion 1000mAh</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Weight:</span>
                        <span>250g</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Connectivity:</span>
                        <span>Bluetooth 5.0, USB-C</span>
                      </div>
                    </div>
                  </div>
                )}

                {activeTab === 'info' && (
                  <div>
                    <h3 className="font-medium text-gray-900 mb-3">Important Information</h3>
                    <div className="space-y-3 text-sm text-gray-600">
                      <div className="flex items-start">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-2"></span>
                        <span>Limited edition product - only 100 units available worldwide</span>
                      </div>
                      <div className="flex items-start">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-2"></span>
                        <span>One item per customer to ensure fair distribution</span>
                      </div>
                      <div className="flex items-start">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-2"></span>
                        <span>Free shipping worldwide with tracking</span>
                      </div>
                      <div className="flex items-start">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-2"></span>
                        <span>2-year warranty included</span>
                      </div>
                      <div className="flex items-start">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-2"></span>
                        <span>30-day money-back guarantee</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Right Column - Purchase Section */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-lg p-6 sticky top-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Purchase Section</h3>

              {/* User ID Input */}
              <div className="mb-4">
                <label htmlFor="userId" className="block text-sm font-medium text-gray-700 mb-2">
                  User ID
                </label>
                <input
                  type="text"
                  id="userId"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  placeholder="Enter your user ID"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              {/* Purchase Button */}
              <button
                onClick={handlePurchase}
                disabled={loading || !status || status.status !== 'active'}
                className={`w-full py-3 px-4 rounded-md font-medium text-white transition-all duration-200 ${
                  loading || !status || status.status !== 'active'
                    ? 'bg-gray-400 cursor-not-allowed'
                    : 'bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 transform hover:scale-105 shadow-lg hover:shadow-xl'
                }`}
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    Processing...
                  </div>
                ) : (
                  'Buy Now - $99.99'
                )}
              </button>

              {/* Status Message */}
              {status && status.status !== 'active' && (
                <div className="mt-4 p-3 rounded-md bg-yellow-50 border border-yellow-200">
                  <p className="text-sm text-yellow-800">
                    {status.status === 'upcoming' && 'Sale has not started yet'}
                    {status.status === 'ended' && 'Sale has ended'}
                    {status.status === 'sold-out' && 'All items have been sold'}
                  </p>
                </div>
              )}

              {/* Purchase Result */}
              {purchaseResult && (
                <div className={`mt-4 p-3 rounded-md ${
                  purchaseResult.success 
                    ? 'bg-green-50 border border-green-200' 
                    : 'bg-red-50 border border-red-200'
                }`}>
                  <p className={`text-sm ${
                    purchaseResult.success ? 'text-green-800' : 'text-red-800'
                  }`}>
                    {purchaseResult.message}
                  </p>
