import React, { useState, useEffect } from 'react';
import './App.css';

interface FlashSaleStatus {
  status: 'upcoming' | 'active' | 'sold-out' | 'ended';
  startTime: string;
  endTime: string;
  currentStock: number;
  maxStock: number;
  timeUntilStart?: number;
  timeUntilEnd?: number;
}

interface PurchaseResult {
  success: boolean;
  message: string;
  timestamp: string;
  userId: string;
  purchaseId?: string;
}

interface UserPurchaseStatus {
  hasPurchased: boolean;
  purchaseId?: string;
}

const App: React.FC = () => {
  const [flashSaleStatus, setFlashSaleStatus] = useState<FlashSaleStatus | null>(null);
  const [userId, setUserId] = useState('');
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<UserPurchaseStatus | null>(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState<'detail' | 'specification' | 'important'>('detail');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Sample product images - you can replace these with actual product images
  const productImages = [
    'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1526170375885-4d8ecf77b99f?w=500&h=500&fit=crop',
    'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=500&h=500&fit=crop'
  ];

  useEffect(() => {
    fetchFlashSaleStatus();
  }, []);

  const fetchFlashSaleStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/status');
      if (response.ok) {
        const data = await response.json();
        setFlashSaleStatus(data);
      }
    } catch (error) {
      console.error('Failed to fetch flash sale status:', error);
    }
  };

  const checkUserPurchase = async () => {
    if (!userId.trim()) return;

    try {
      const response = await fetch(`http://localhost:3001/api/flash-sale/purchase/${userId}`);
      if (response.ok) {
        const data = await response.json();
        setUserPurchaseStatus(data);
      }
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const attemptPurchase = async () => {
    if (!userId.trim()) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/flash-sale/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId }),
      });

      if (response.ok) {
        const data = await response.json();
        setPurchaseResult(data);
        if (data.success) {
          await checkUserPurchase();
        }
        await fetchFlashSaleStatus();
      }
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'Purchase failed. Please try again.',
        timestamp: new Date().toISOString(),
        userId,
      });
    } finally {
      setLoading(false);
    }
  };

  const formatTimeRemaining = (milliseconds: number): string => {
    const hours = Math.floor(milliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((milliseconds % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((milliseconds % (1000 * 60)) / 1000);
    return `${hours}h ${minutes}m ${seconds}s`;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-100';
      case 'active': return 'text-green-600 bg-green-100';
      case 'sold-out': return 'text-red-600 bg-red-100';
      case 'ended': return 'text-gray-600 bg-gray-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'upcoming': return 'Coming Soon';
      case 'active': return 'Live Now';
      case 'sold-out': return 'Sold Out';
      case 'ended': return 'Ended';
      default: return 'Unknown';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">
            ðŸš€ Flash Sale System
          </h1>
          <p className="text-lg text-gray-600">
            Limited Edition Product - Don't Miss Out!
          </p>
        </div>

        {/* Main Content - 3 Column Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">

          {/* Left Column - Image Slider */}
          <div className="lg:col-span-5">
            <div className="bg-white rounded-2xl shadow-lg p-6">
              <div className="relative">
                {/* Main Image */}
                <div className="aspect-square rounded-xl overflow-hidden mb-4">
                  <img
                    src={productImages[currentImageIndex]}
                    alt="Product"
                    className="w-full h-full object-cover"
                  />
                </div>

                {/* Thumbnail Navigation */}
                <div className="flex space-x-2 justify-center">
                  {productImages.map((image, index) => (
                    <button
                      key={index}
                      onClick={() => setCurrentImageIndex(index)}
                      className={`w-16 h-16 rounded-lg overflow-hidden border-2 transition-all ${
                        currentImageIndex === index
                          ? 'border-blue-500 scale-110'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <img
                        src={image}
                        alt={`Product ${index + 1}`}
                        className="w-full h-full object-cover"
                      />
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Center Column - Product Details */}
          <div className="lg:col-span-4">
            <div className="bg-white rounded-2xl shadow-lg p-6">
              <h2 className="text-2xl font-bold text-gray-900 mb-2">
                Premium Wireless Headphones
              </h2>
              <div className="flex items-center mb-4">
                <div className="flex text-yellow-400">
                  {[...Array(5)].map((_, i) => (
                    <svg key={i} className="w-5 h-5 fill-current" viewBox="0 0 20 20">
                      <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z"/>
                    </svg>
                  ))}
                </div>
                <span className="ml-2 text-sm text-gray-600">4.8 (127 reviews)</span>
              </div>

              <div className="text-3xl font-bold text-green-600 mb-6">
                $99.99
              </div>

              {/* Tabs */}
              <div className="border-b border-gray-200 mb-6">
                <nav className="flex space-x-8">
                  {[
                    { id: 'detail', label: 'Detail' },
                    { id: 'specification', label: 'Specification' },
                    { id: 'important', label: 'Important Info' }
                  ].map((tab) => (
                    <button
                      key={tab.id}
                      onClick={() => setActiveTab(tab.id as any)}
                      className={`py-2 px-1 border-b-2 font-medium text-sm transition-colors ${
                        activeTab === tab.id
                          ? 'border-blue-500 text-blue-600'
                          : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                      }`}
                    >
                      {tab.label}
                    </button>
                  ))}
                </nav>
              </div>

              {/* Tab Content */}
              <div className="min-h-[200px]">
                {activeTab === 'detail' && (
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Condition:</span>
                      <span className="font-medium">New</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Category:</span>
                      <span className="font-medium">Electronics</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Brand:</span>
                      <span className="font-medium">Premium Audio</span>
                    </div>
                    <p className="text-gray-700 leading-relaxed">
                      Experience crystal-clear sound with our premium wireless headphones. 
                      Features include noise cancellation, 30-hour battery life, and 
                      premium comfort for extended listening sessions.
                    </p>
                  </div>
                )}

                {activeTab === 'specification' && (
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Driver Size:</span>
                      <span className="font-medium">40mm</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Frequency Response:</span>
                      <span className="font-medium">20Hz - 20kHz</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Battery Life:</span>
                      <span className="font-medium">30 hours</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Connectivity:</span>
                      <span className="font-medium">Bluetooth 5.0</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Weight:</span>
                      <span className="font-medium">250g</span>
                    </div>
                  </div>
                )}

                {activeTab === 'important' && (
                  <div className="space-y-3">
                    <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4">
                      <div className="flex">
                        <div className="flex-shrink-0">
                          <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                          </svg>
                        </div>
                        <div className="ml-3">
                          <p className="text-sm text-yellow-700">
                            <strong>Important:</strong> This is a limited edition product. 
                            Only one item per customer is allowed.
                          </p>
                        </div>
                      </div>
                    </div>
                    <div className="text-sm text-gray-600 space-y-2">
                      <p>â€¢ Free shipping on all orders</p>
                      <p>â€¢ 30-day money-back guarantee</p>
                      <p>â€¢ 2-year manufacturer warranty</p>
                      <p>â€¢ Customer support available 24/7</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Right Column - Floating Purchase Section */}
          <div className="lg:col-span-3">
            <div className="sticky top-8">
              <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Purchase Section
                </h3>

                {/* Flash Sale Status */}
                {flashSaleStatus && (
                  <div className="mb-6">
                    <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium mb-3 ${getStatusColor(flashSaleStatus.status)}`}>
                      {getStatusText(flashSaleStatus.status)}
                    </div>

                    {flashSaleStatus.status === 'upcoming' && flashSaleStatus.timeUntilStart && (
                      <div className="text-center p-3 bg-blue-50 rounded-lg mb-4">
                        <p className="text-sm text-blue-600 font-medium">Sale starts in:</p>
                        <p className="text-lg font-bold text-blue-800">
                          {formatTimeRemaining(flashSaleStatus.timeUntilStart)}
                        </p>
                      </div>
                    )}

                    {flashSaleStatus.status === 'active' && flashSaleStatus.timeUntilEnd && (
                      <div className="text-center p-3 bg-green-50 rounded-lg mb-4">
                        <p className="text-sm text-green-600 font-medium">Sale ends in:</p>
                        <p className="text-lg font-bold text-green-800">
                          {formatTimeRemaining(flashSaleStatus.timeUntilEnd)}
                        </p>
                      </div>
                    )}

                    <div className="flex justify-between items-center mb-4">
                      <span className="text-gray-600">Stock:</span>
                      <span className="font-semibold text-gray-900">
                        {flashSaleStatus.currentStock} / {flashSaleStatus.maxStock}
                      </span>
                    </div>

                    {/* Progress Bar */}
                    <div className="w-full bg-gray-200 rounded-full h-2 mb-4">
                      <div 
                        className="bg-green-500 h-2 rounded-full transition-all duration-300"
                        style={{ width: `${(flashSaleStatus.currentStock / flashSaleStatus.maxStock) * 100}%` }}
                      ></div>
                    </div>
                  </div>
                )}

                {/* User ID Input */}
                <div className="mb-4">
                  <label htmlFor="userId" className="block text-sm font-medium text-gray-700 mb-2">
                    User ID
                  </label>
                  <input
                    type="text"
                    id="userId"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                    placeholder="Enter your user ID"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                {/* Check Purchase Button */}
                <button
                  onClick={checkUserPurchase}
                  disabled={!userId.trim()}
                  className="w-full mb-4 px-4 py-2 bg-gray-100 text-gray-700 rounded-lg font-medium hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  Check Purchase Status
                </button>

                {/* Purchase Status Display */}
                {userPurchaseStatus && (
                  <div className={`p-3 rounded-lg mb-4 ${
                    userPurchaseStatus.hasPurchased 
                      ? 'bg-green-50 border border-green-200' 
                      : 'bg-blue-50 border border-blue-200'
                  }`}>
                    <p className={`text-sm font-medium ${
                      userPurchaseStatus.hasPurchased ? 'text-green-800' : 'text-blue-800'
                    }`}>
                      {userPurchaseStatus.hasPurchased 
                        ? `âœ… Already purchased! ID: ${userPurchaseStatus.purchaseId}` 
                        : 'ðŸ†• Ready to purchase!'
                      }
                    </p>
                  </div>
                )}

                {/* Buy Now Button */}
                <button
                  onClick={attemptPurchase}
                  disabled={!userId.trim() || loading || (userPurchaseStatus?.hasPurchased) || flashSaleStatus?.status !== 'active'}
                  className={`w-full px-6 py-3 rounded-lg font-bold text-white transition-all duration-200 ${
                    flashSaleStatus?.status === 'active' && !userPurchaseStatus?.hasPurchased
                      ? 'bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 transform hover:scale-105 shadow-lg'
                      : 'bg-gray-400 cursor-not-allowed'
                  }`}
                >
                  {loading ? (
                    <div className="flex items-center justify-center">
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      Processing...
                    </div>
                  ) : (
                    'ðŸ”¥ BUY NOW!'
                  )}
                </button>

                {/* Purchase Result */}
                {purchaseResult && (
                  <div className={`mt-4 p-3 rounded-lg ${
                    purchaseResult.success 
                      ? 'bg-green-50 border border-green-200' 
                      : 'bg-red-50 border border-red-200'
                  }`}>
                    <p className={`text-sm font-medium ${
                      purchaseResult.success ? 'text-green-800' : 'text-red-800'
                    }`}>
                      {purchaseResult.message}
                    </p>
                    {purchaseResult.success && purchaseResult.purchaseId && (
                      <p className="text-xs text-green-600 mt-1">
                        Purchase ID: {purchaseResult.purchaseId}
                      </p>
                    )}
                  </div>
                )}

                {/* Price Display */}
                <div className="mt-6 pt-4 border-t border-gray-200">
                  <div className="flex justify-between items-center">
                    <span className="text-lg font-medium text-gray-900">Total:</span>
                    <span className="text-2xl font-bold text-green-600">$99.99</span>
                  </div>
                  <p className="text-xs text-gray-500 mt-1">Free shipping included</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
