import React, { useState, useEffect } from 'react';
import './App.css';
import { FlashSaleStatus, PurchaseResult } from './types';
import { FlashSaleAPI } from './services/api';

function App() {
  const [status, setStatus] = useState<FlashSaleStatus | null>(null);
  const [userId, setUserId] = useState('');
  const [purchaseResult, setPurchaseResult] = useState<PurchaseResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [userPurchaseStatus, setUserPurchaseStatus] = useState<{ hasPurchased: boolean; purchaseId?: string } | null>(null);
  const [quantity, setQuantity] = useState(1);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [autoSlide, setAutoSlide] = useState(true);

  // Product details for the flash sale
  const productDetails = {
    name: "ASUS ROG PHONE 8 (12/256GB) - Snapdragon8 Gen3, HP Gaming",
    description: "Smartphone gaming terbaru dengan performa maksimal untuk gaming dan multitasking. Dilengkapi dengan Snapdragon 8 Gen 3, kamera 50MP, dan baterai 5500mAh.",
    originalPrice: 15000000,
    flashSalePrice: 10000000,
    savings: 5000000,
    stock: 100,
    sold: 750,
    condition: "Baru",
    minOrder: 1,
    warranty: "Garansi Resmi Indonesia",
    features: [
      "Snapdragon 8 Gen 3 Processor",
      "6.78\" AMOLED 165Hz Display",
      "50MP + 13MP + 5MP Triple Camera",
      "32MP Front Camera",
      "5500mAh Battery",
      "12GB RAM + 256GB Storage",
      "Android 14",
      "Gaming Mode & RGB Lighting"
    ],
    images: [
      "/images/rog-phone-1.svg",
      "/images/rog-phone-2.svg", 
      "/images/rog-phone-3.svg",
      "/images/rog-phone-4.svg",
      "/images/rog-phone-5.svg"
    ],
    specifications: {
      "Processor": "Snapdragon 8 Gen 3",
      "Display": "6.78\" AMOLED 165Hz",
      "RAM": "12GB LPDDR5X",
      "Storage": "256GB UFS 4.0",
      "Main Camera": "50MP + 13MP + 5MP",
      "Front Camera": "32MP",
      "Battery": "5500mAh, 65W Fast Charging",
      "OS": "Android 14, ROG UI",
      "Connectivity": "5G, WiFi 7, Bluetooth 5.3",
      "Security": "In-display Fingerprint, Face Recognition"
    }
  };

  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  // Auto-slide effect for image slider
  useEffect(() => {
    if (!autoSlide) return;

    const interval = setInterval(() => {
      nextImage();
    }, 4000); // Change image every 4 seconds

    return () => clearInterval(interval);
  }, [autoSlide, currentImageIndex]);

  // Pause auto-slide on hover
  const handleImageHover = () => setAutoSlide(false);
  const handleImageLeave = () => setAutoSlide(true);

  const fetchStatus = async () => {
    try {
      const statusData = await FlashSaleAPI.getStatus();
      setStatus(statusData);
    } catch (error) {
      console.error('Failed to fetch status:', error);
    }
  };

  const handlePurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    setLoading(true);
    try {
      const result = await FlashSaleAPI.attemptPurchase(userId);
      setPurchaseResult(result);

      if (result.success) {
        setUserPurchaseStatus({ hasPurchased: true, purchaseId: result.purchaseId });
      }

      fetchStatus();
    } catch (error) {
      console.error('Purchase failed:', error);
      setPurchaseResult({
        success: false,
        message: 'An error occurred during purchase',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const checkUserPurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    try {
      const result = await FlashSaleAPI.checkUserPurchase(userId);
      setUserPurchaseStatus(result);
    } catch (error) {
      console.error('Failed to check user purchase:', error);
    }
  };

  const formatTime = (milliseconds: number | undefined) => {
    if (!milliseconds) return '';

    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m ${seconds % 60}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming': return 'text-blue-600 bg-blue-50 border-blue-200';
      case 'active': return 'text-green-600 bg-green-50 border-green-200';
      case 'sold-out': return 'text-red-600 bg-red-50 border-red-200';
      case 'ended': return 'text-gray-600 bg-gray-50 border-gray-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const getStatusGradient = (status: string) => {
    switch (status) {
      case 'upcoming': return 'from-blue-500 to-blue-600';
      case 'active': return 'from-green-500 to-green-600';
      case 'sold-out': return 'from-red-500 to-red-600';
      case 'ended': return 'from-gray-500 to-gray-600';
      default: return 'from-gray-500 to-gray-600';
    }
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(price);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'upcoming': return '⏰';
      case 'active': return '🔥';
      case 'sold-out': return '❌';
      case 'ended': return '🏁';
      default: return '❓';
    }
  };

  const nextImage = () => {
    setCurrentImageIndex((prev) => 
      prev === productDetails.images.length - 1 ? 0 : prev + 1
    );
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => 
      prev === 0 ? productDetails.images.length - 1 : prev - 1
    );
  };

  const selectImage = (index: number) => {
    setCurrentImageIndex(index);
    setAutoSlide(false); // Pause auto-slide when user manually selects
    // Resume auto-slide after 3 seconds
    setTimeout(() => setAutoSlide(true), 3000);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 sticky top-0 z-50">
        <div className="container mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <h1 className="text-2xl font-bold text-gray-800">⚡ Flash Sale System</h1>
            </div>
            <div className="text-sm text-gray-600">
              High-Throughput Limited Edition Product Sale
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-6">
        {/* Breadcrumb */}
        <div className="text-sm text-gray-500 mb-4">
          Home {'>'} Handphone & Tablet {'>'} Handphone {'>'} Android OS {'>'} {productDetails.name}
        </div>

        {/* Main Product Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
                      {/* Left Column - Product Images */}
            <div className="space-y-4">
              {/* Main Product Image Slider - Tokopedia Style */}
              <div className="relative bg-white rounded-lg border border-gray-200 overflow-hidden">
                {/* Main Image Container */}
                <div 
                  className="relative"
                  onMouseEnter={handleImageHover}
                  onMouseLeave={handleImageLeave}
                >
                  <img 
                    src={productDetails.images[currentImageIndex]} 
                    alt={`${productDetails.name} - Image ${currentImageIndex + 1}`}
                    className="w-full h-96 object-contain bg-gray-50 transition-all duration-300"
                  />

                  {/* Navigation Arrows - Tokopedia Style */}
                  <button 
                    onClick={prevImage}
                    className="absolute left-3 top-1/2 transform -translate-y-1/2 bg-white/90 hover:bg-white text-gray-700 w-8 h-8 rounded-full flex items-center justify-center shadow-lg transition-all duration-200 hover:scale-105 border border-gray-200"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                  </button>
                  <button 
                    onClick={nextImage}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 bg-white/90 hover:bg-white text-gray-700 w-8 h-8 rounded-full flex items-center justify-center shadow-lg transition-all duration-200 hover:scale-105 border border-gray-200"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </button>

                  {/* Image Counter - Tokopedia Style */}
                  <div className="absolute bottom-3 right-3 bg-black/60 text-white px-2 py-1 rounded-full text-xs font-medium">
                    {currentImageIndex + 1} dari {productDetails.images.length}
                  </div>

                  {/* Auto-slide Progress Bar */}
                  <div className="absolute bottom-0 left-0 right-0 h-1 bg-gray-200">
                    <div 
                      className="h-full bg-red-500 transition-all duration-100 ease-linear"
                      style={{ 
                        width: `${((currentImageIndex + 1) / productDetails.images.length) * 100}%` 
                      }}
                    />
                  </div>

                  {/* Flash Sale Badge - Tokopedia Style */}
                  <div className="absolute top-3 left-3">
                    <span className="bg-gradient-to-r from-red-500 to-red-600 text-white px-3 py-1.5 rounded-full text-xs font-bold shadow-lg">
                      🔥 FLASH SALE
                    </span>
                  </div>

                  {/* Extra Discount Badge - Tokopedia Style */}
                  <div className="absolute bottom-3 left-3">
                    <div className="bg-red-600 text-white px-3 py-2 rounded-lg text-xs font-bold shadow-lg">
                      <div className="text-center">
                        <div className="text-xs">EXTRA DISCOUNT</div>
                        <div className="text-sm">UP TO {formatPrice(5000000)}</div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Thumbnail Navigation - Tokopedia Style */}
              <div className="flex space-x-2 overflow-x-auto pb-2">
                {productDetails.images.map((image, index) => (
                  <button
                    key={index}
                    onClick={() => selectImage(index)}
                    className={`flex-shrink-0 w-20 h-20 rounded-lg overflow-hidden border-2 transition-all duration-200 ${
                      index === currentImageIndex 
                        ? 'border-red-500 ring-2 ring-red-200' 
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <img 
                      src={image} 
                      alt={`${productDetails.name} thumbnail ${index + 1}`}
                      className="w-full h-full object-cover"
                    />
                  </button>
                ))}
              </div>

              {/* Dots Indicator - Tokopedia Style */}
              <div className="flex justify-center space-x-1.5">
                {productDetails.images.map((_, index) => (
                  <button
                    key={index}
                    onClick={() => selectImage(index)}
                    className={`w-2 h-2 rounded-full transition-all duration-200 ${
                      index === currentImageIndex 
                        ? 'bg-red-500' 
                        : 'bg-gray-300 hover:bg-gray-400'
                    }`}
                  />
                ))}
              </div>
            </div>

          {/* Right Column - Product Info & Purchase */}
          <div className="space-y-6">
            {/* Product Title */}
            <div>
              <h2 className="text-2xl font-bold text-gray-800 mb-2">{productDetails.name}</h2>
              <p className="text-gray-600 text-sm mb-2">{productDetails.description}</p>
              <div className="flex items-center space-x-2 text-sm text-gray-500">
                <span>📦 {productDetails.sold}+ barang berhasil terjual</span>
              </div>
            </div>

            {/* Pricing Section */}
            <div className="bg-white p-6 rounded-lg border border-gray-200">
              <div className="space-y-3">
                <div className="flex items-baseline space-x-3">
                  <span className="text-3xl font-bold text-red-600">{formatPrice(productDetails.flashSalePrice)}</span>
                  <span className="text-lg text-gray-500 line-through">{formatPrice(productDetails.originalPrice)}</span>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="bg-red-100 text-red-700 px-2 py-1 rounded text-sm font-medium">
                    Hemat {formatPrice(productDetails.savings)}
                  </span>
                </div>
              </div>
            </div>

            {/* Flash Sale Status */}
            {status && (
              <div className="bg-white p-6 rounded-lg border border-gray-200">
                <div className="text-center mb-4">
                  <div className="text-4xl mb-3">{getStatusIcon(status.status)}</div>
                  <div className={`inline-flex items-center px-4 py-2 rounded-full border-2 ${getStatusColor(status.status)}`}>
                    <h3 className={`text-lg font-bold ${getStatusColor(status.status).split(' ')[0]} mb-0`}>
                      {status.status.charAt(0).toUpperCase() + status.status.slice(1)}
                    </h3>
                  </div>
                </div>

                {status.status === 'upcoming' && status.timeUntilStart && (
                  <div className="bg-gradient-to-r from-blue-500 to-purple-600 text-white p-3 rounded-lg text-center">
                    ⏱️ Mulai dalam: {formatTime(status.timeUntilStart)}
                  </div>
                )}
                {status.status === 'active' && status.timeUntilEnd && (
                  <div className="bg-gradient-to-r from-green-500 to-emerald-600 text-white p-3 rounded-lg text-center">
                    ⏰ Berakhir dalam: {formatTime(status.timeUntilEnd)}
                  </div>
                )}

                {/* Stock Progress */}
                <div className="mt-4">
                  <div className="flex justify-between text-sm text-gray-600 mb-2">
                    <span>Progress Penjualan</span>
                    <span>{Math.round(((status.maxStock - status.currentStock) / status.maxStock) * 100)}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className={`h-2 rounded-full transition-all duration-500 ${getStatusGradient(status.status)}`}
                      style={{ width: `${((status.maxStock - status.currentStock) / status.maxStock) * 100}%` }}
                    ></div>
                  </div>
                  <div className="grid grid-cols-3 gap-4 mt-4 text-center">
                    <div>
                      <div className="text-lg font-bold text-blue-600">{status.maxStock}</div>
                      <div className="text-xs text-gray-600">Total Stock</div>
                    </div>
                    <div>
                      <div className="text-lg font-bold text-green-600">{status.currentStock}</div>
                      <div className="text-xs text-gray-600">Tersedia</div>
                    </div>
                    <div>
                      <div className="text-lg font-bold text-purple-600">{status.maxStock - status.currentStock}</div>
                      <div className="text-xs text-gray-600">Terjual</div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Purchase Section */}
            <div className="bg-white p-6 rounded-lg border border-gray-200">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Atur jumlah dan catatan</h3>

              {/* Product Summary - Tokopedia Style */}
              <div className="flex items-center space-x-3 mb-4 p-4 bg-gray-50 rounded-lg border border-gray-200">
                <img 
                  src={productDetails.images[currentImageIndex]} 
                  alt={productDetails.name}
                  className="w-20 h-20 object-contain bg-white rounded-lg border border-gray-200"
                />
                <div className="flex-1">
                  <p className="text-sm font-semibold text-gray-800">{productDetails.name}</p>
                  <p className="text-xs text-gray-600 mt-1">Flash Sale - Limited Edition</p>
                  <div className="flex items-center mt-2 space-x-3">
                    <span className="text-xs text-gray-500">Stock: {productDetails.stock.toLocaleString()}</span>
                    <span className="text-xs text-gray-500">Terjual: {productDetails.sold.toLocaleString()}</span>
                  </div>
                </div>
              </div>

              {/* Quantity Selector */}
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">Jumlah</label>
                <div className="flex items-center space-x-3">
                  <button 
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    className="w-8 h-8 rounded-full border border-gray-300 flex items-center justify-center hover:bg-gray-50"
                  >
                    -
                  </button>
                  <input
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                    className="w-20 text-center border border-gray-300 rounded-lg px-2 py-1"
                    min="1"
                  />
                  <button 
                    onClick={() => setQuantity(quantity + 1)}
                    className="w-8 h-8 rounded-full border border-gray-300 flex items-center justify-center hover:bg-gray-50"
                  >
                    +
                  </button>
                  <span className="text-sm text-gray-600">Stok: {productDetails.stock.toLocaleString()}</span>
                </div>
              </div>

              {/* Subtotal */}
              <div className="border-t border-gray-200 pt-4 mb-4">
                <div className="flex justify-between items-center">
                  <span className="text-lg font-medium text-gray-800">Subtotal</span>
                  <span className="text-xl font-bold text-red-600">
                    {formatPrice(productDetails.flashSalePrice * quantity)}
                  </span>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="space-y-3">
                <button
                  onClick={handlePurchase}
                  disabled={loading || status?.status !== 'active'}
                  className={`w-full py-3 rounded-lg font-semibold text-lg transition-all duration-200 ${
                    loading || status?.status !== 'active'
                      ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                      : 'bg-gradient-to-r from-green-500 to-green-600 text-white hover:from-green-600 hover:to-green-700 shadow-lg'
                  }`}
                >
                  {loading ? (
                    <div className="flex items-center justify-center">
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      Memproses...
                    </div>
                  ) : (
                    '🚀 Beli Langsung'
                  )}
                </button>

                <button
                  onClick={checkUserPurchase}
                  disabled={!userId.trim()}
                  className={`w-full py-3 rounded-lg font-semibold text-lg transition-all duration-200 ${
                    !userId.trim()
                      ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                      : 'bg-gradient-to-r from-blue-500 to-blue-600 text-white hover:from-blue-600 hover:to-blue-700 shadow-lg'
                  }`}
                >
                  🔍 Cek Status Pembelian
                </button>
              </div>

              {/* User ID Input */}
              <div className="mt-4">
                <label htmlFor="userId" className="block text-sm font-medium text-gray-700 mb-2">
                  User ID
                </label>
                <input
                  type="text"
                  id="userId"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  placeholder="Masukkan User ID Anda"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Product Details Tabs */}
        <div className="bg-white rounded-lg border border-gray-200 mb-8">
          <div className="border-b border-gray-200">
            <div className="flex">
              <button className="px-6 py-3 text-sm font-medium text-green-600 border-b-2 border-green-600">
                Detail
              </button>
              <button className="px-6 py-3 text-sm font-medium text-gray-500 hover:text-gray-700">
                Spesifikasi
              </button>
            </div>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-gray-600">Kondisi:</span>
                  <span className="font-medium">{productDetails.condition}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Min. Pemesanan:</span>
                  <span className="font-medium">{productDetails.minOrder} Buah</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Garansi:</span>
                  <span className="font-medium">{productDetails.warranty}</span>
                </div>
              </div>

              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-gray-600">Etalase:</span>
                  <span className="font-medium">Semua Etalase</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Kategori:</span>
                  <span className="font-medium">Handphone Gaming</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Brand:</span>
                  <span className="font-medium">ASUS ROG</span>
                </div>
              </div>
            </div>

            {/* Why Shop Here */}
            <div className="mt-6 p-4 bg-blue-50 rounded-lg">
              <h4 className="font-semibold text-blue-800 mb-2">Kenapa Harus Belanja di Flash Sale System?</h4>
              <ul className="text-sm text-blue-700 space-y-1">
                <li>• Ada Kendala Unit, Kita siap membantu</li>
                <li>• Barang 100% Baru dan Bergaransi Resmi</li>
                <li>• Free Packing Bubble Tebal 100% dijamin aman</li>
                <li>• Flash Sale dengan harga terbaik</li>
                <li>• Sistem high-throughput untuk pembelian cepat</li>
              </ul>
            </div>
          </div>
        </div>

        {/* Specifications */}
        <div className="bg-white rounded-lg border border-gray-200 mb-8">
          <div className="p-6">
            <h3 className="text-xl font-bold text-gray-800 mb-6">📋 Spesifikasi Teknis</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {Object.entries(productDetails.specifications).map(([key, value]) => (
                <div key={key} className="bg-gray-50 p-4 rounded-lg">
                  <dt className="font-semibold text-gray-800 mb-1">{key}</dt>
                  <dd className="text-gray-600 text-sm">{value}</dd>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Purchase Results */}
        {(purchaseResult || userPurchaseStatus) && (
          <div className="bg-white rounded-lg border border-gray-200 p-6 mb-8">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Hasil Pembelian</h3>

            {/* Purchase Result */}
            {purchaseResult && (
              <div className={`${
                purchaseResult.success 
                  ? 'bg-gradient-to-r from-green-50 to-emerald-50 border-2 border-green-200 text-green-800' 
                  : 'bg-gradient-to-r from-red-50 to-pink-50 border-2 border-red-200 text-red-800'
              } p-4 rounded-lg mb-4`}
                data-testid="purchase-result"
              >
                <div className="font-bold text-lg mb-2">{purchaseResult.message}</div>
                {purchaseResult.purchaseId && (
                  <div className="text-sm bg-white/50 px-3 py-2 rounded-lg inline-block">
                    🆔 Purchase ID: {purchaseResult.purchaseId}
                  </div>
                )}
                <div className="text-sm mt-3 opacity-75">
                  ⏰ {new Date(purchaseResult.timestamp).toLocaleString()}
                </div>
              </div>
            )}

            {/* User Purchase Status */}
            {userPurchaseStatus && (
              <div className={`${
                userPurchaseStatus.hasPurchased 
                  ? 'bg-gradient-to-r from-blue-50 to-indigo-50 border-2 border-blue-200 text-blue-800' 
                  : 'bg-gradient-to-r from-gray-50 to-slate-50 border-2 border-gray-200 text-gray-800'
              } p-4 rounded-lg`}
                data-testid="user-purchase-status"
              >
                <div className="font-bold text-lg mb-2">
                  {userPurchaseStatus.hasPurchased 
                    ? `🎉 Selamat! Anda telah berhasil membeli produk!` 
                    : `ℹ️ Tidak ada pembelian ditemukan untuk user: ${userId}`
                  }
                </div>
                {userPurchaseStatus.purchaseId && (
                  <div className="text-sm bg-white/50 px-3 py-2 rounded-lg inline-block">
                    🆔 Purchase ID: {userPurchaseStatus.purchaseId}
                  </div>
                )}
              </div>
            )}
          </div>
        )}

        {/* System Information */}
        <div className="bg-white rounded-lg border border-gray-200 p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-6">⚙️ Informasi Sistem</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-gradient-to-br from-blue-50 to-indigo-50 p-4 rounded-lg border border-blue-200">
              <h4 className="font-semibold text-blue-800 mb-3">🖥️ Backend Technology</h4>
              <div className="space-y-2 text-sm text-blue-700">
                <p><strong>Framework:</strong> Node.js + NestJS + Express</p>
                <p><strong>Database:</strong> Redis + PostgreSQL</p>
                <p><strong>Message Queue:</strong> Kafka (Redpanda)</p>
              </div>
            </div>
            <div className="bg-gradient-to-br from-green-50 to-emerald-50 p-4 rounded-lg border border-green-200">
              <h4 className="font-semibold text-green-800 mb-3">🎨 Frontend Technology</h4>
              <div className="space-y-2 text-sm text-green-700">
                <p><strong>Framework:</strong> React + TypeScript</p>
                <p><strong>Styling:</strong> Tailwind CSS</p>
                <p><strong>State Management:</strong> React Hooks</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
