import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const BiddingDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [userId, setUserId] = useState('');
  const [loading, setLoading] = useState(false);
  const [purchaseResult, setPurchaseResult] = useState<any>(null);

  // Mock product data - in real app this would come from API
  const product = {
    id: id,
    name: "Limited Edition Smartphone Pro Max",
    price: 999,
    originalPrice: 1299,
    discount: "23% OFF",
    status: "upcoming",
    description: "Experience the future of mobile technology with our Limited Edition Smartphone Pro Max. Featuring cutting-edge AI capabilities, stunning 6.7-inch OLED display, and revolutionary camera system.",
    features: [
      "6.7-inch Super Retina XDR OLED display",
      "A17 Pro chip with 6-core GPU",
      "48MP Main camera with 2x Telephoto",
      "12MP Ultra Wide camera",
      "12MP TrueDepth front camera",
      "USB-C connector",
      "Face ID",
      "Emergency SOS via satellite",
      "Crash Detection",
      "Up to 29 hours video playback"
    ],
    images: [
      "https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=600&h=600&fit=crop",
      "https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=600&h=600&fit=crop&crop=face",
      "https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=600&h=600&fit=crop&crop=entropy",
      "https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=600&h=600&fit=crop&crop=edges"
    ],
    stock: 100,
    sold: 0,
    startTime: "2025-08-23T12:00:00Z",
    endTime: "2025-08-23T18:00:00Z"
  };

  const handlePurchase = async () => {
    if (!userId.trim()) {
      alert('Please enter a user ID');
      return;
    }

    setLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      setPurchaseResult({
        success: true,
        message: 'Purchase successful!',
        purchaseId: 'PUR-' + Date.now(),
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      setPurchaseResult({
        success: false,
        message: 'Purchase failed. Please try again.',
        timestamp: new Date().toISOString()
      });
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (milliseconds: number) => {
    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m ${seconds % 60}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getTimeUntilStart = () => {
    const now = new Date();
    const start = new Date(product.startTime);
    return Math.max(0, start.getTime() - now.getTime());
  };

  const getTimeUntilEnd = () => {
    const now = new Date();
    const end = new Date(product.endTime);
    return Math.max(0, end.getTime() - now.getTime());
  };

  const timeUntilStart = getTimeUntilStart();
  const timeUntilEnd = getTimeUntilEnd();

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Link to="/" className="text-2xl font-bold text-gray-900">FlashSale</Link>
            </div>
            <div className="flex items-center space-x-4">
              <button className="text-gray-500 hover:text-gray-700 px-3 py-2 rounded-md text-sm font-medium">
                Sign In
              </button>
              <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-medium">
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

          {/* Column 1: Image Slider with Thumbnails */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-lg p-6">
              {/* Main Image */}
              <div className="mb-4">
                <img 
                  src={product.images[currentImageIndex]} 
                  alt={`${product.name} - Image ${currentImageIndex + 1}`}
                  className="w-full h-96 object-cover rounded-lg"
                />
              </div>

              {/* Thumbnails */}
              <div className="grid grid-cols-4 gap-2">
                {product.images.map((image, index) => (
                  <button
                    key={index}
                    onClick={() => setCurrentImageIndex(index)}
                    className={`w-full h-20 rounded-lg overflow-hidden border-2 transition-all ${
                      currentImageIndex === index 
                        ? 'border-blue-500 ring-2 ring-blue-200' 
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <img 
                      src={image} 
                      alt={`Thumbnail ${index + 1}`}
                      className="w-full h-full object-cover"
                    />
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Column 2: Product Details */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-lg p-6">
              <div className="mb-6">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">{product.name}</h1>
                <div className="flex items-center mb-4">
                  <span className="text-3xl font-bold text-red-600">${product.price}</span>
                  <span className="text-xl text-gray-500 line-through ml-3">${product.originalPrice}</span>
                  <span className="bg-red-500 text-white text-sm font-bold px-3 py-1 rounded-full ml-3">
                    {product.discount}
                  </span>
                </div>

                {/* Status Badge */}
                <div className="mb-4">
                  {product.status === 'upcoming' && (
                    <span className="bg-blue-100 text-blue-800 text-sm font-medium px-3 py-1 rounded-full">
                      ‚è∞ Sale starts in {formatTime(timeUntilStart)}
                    </span>
                  )}
                  {product.status === 'active' && (
                    <span className="bg-green-100 text-green-800 text-sm font-medium px-3 py-1 rounded-full">
                      üî• Sale ends in {formatTime(timeUntilEnd)}
                    </span>
                  )}
                </div>
              </div>

              {/* Description */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Description</h3>
                <p className="text-gray-600 leading-relaxed">{product.description}</p>
              </div>

              {/* Features */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Key Features</h3>
                <ul className="grid grid-cols-1 gap-2">
                  {product.features.map((feature, index) => (
                    <li key={index} className="flex items-center text-gray-600">
                      <svg className="w-4 h-4 text-green-500 mr-2 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                      </svg>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Stock Information */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Stock Information</h3>
                <div className="grid grid-cols-3 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-blue-600">{product.stock}</div>
                    <div className="text-sm text-gray-600">Available</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600">{product.stock - product.sold}</div>
                    <div className="text-sm text-gray-600">Remaining</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-600">{product.sold}</div>
                    <div className="text-sm text-gray-600">Sold</div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Column 3: Purchase Section (Floating) */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-lg p-6 sticky top-8">
              <h3 className="text-xl font-semibold text-gray-900 mb-4">Purchase Section</h3>

              {/* User ID Input */}
              <div className="mb-4">
                <label htmlFor="userId" className="block text-sm font-medium text-gray-700 mb-2">
                  User ID
                </label>
                <input
                  type="text"
                  id="userId"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  placeholder="Enter your user ID"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              {/* Purchase Button */}
              <button
                onClick={handlePurchase}
                disabled={loading || product.status !== 'active'}
                className={`w-full py-3 px-4 rounded-lg font-medium transition-colors mb-4 ${
                  loading || product.status !== 'active'
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : 'bg-red-600 text-white hover:bg-red-700 focus:ring-2 focus:ring-red-500'
                }`}
              >
                {loading ? 'Processing...' : 'Buy Now!'}
              </button>

              {/* Purchase Result */}
              {purchaseResult && (
                <div className={`${
                  purchaseResult.success 
                    ? 'bg-green-100 border border-green-300 text-green-800' 
                    : 'bg-red-100 border border-red-300 text-red-800'
                } p-4 rounded-lg`}
                >
                  <div className="font-medium">{purchaseResult.message}</div>
                  {purchaseResult.purchaseId && (
                    <div className="text-sm mt-1">Purchase ID: {purchaseResult.purchaseId}</div>
                  )}
                  <div className="text-sm mt-1">{new Date(purchaseResult.timestamp).toLocaleString()}</div>
                </div>
              )}

              {/* Sale Timer */}
              <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                <h4 className="text-sm font-medium text-gray-700 mb-2">Sale Timer</h4>
                {product.status === 'upcoming' ? (
                  <div className="text-center">
                    <div className="text-2xl font-bold text-blue-600">
                      {formatTime(timeUntilStart)}
                    </div>
                    <div className="text-sm text-gray-600">Until sale starts</div>
                  </div>
                ) : product.status === 'active' ? (
                  <div className="text-center">
                    <div className="text-2xl font-bold text-red-600">
                      {formatTime(timeUntilEnd)}
                    </div>
                    <div className="text-sm text-gray-600">Sale ends in</div>
                  </div>
                ) : (
                  <div className="text-center">
                    <div className="text-2xl font-bold text-gray-600">Sale Ended</div>
                    <div className="text-sm text-gray-600">Better luck next time!</div>
                  </div>
                )}
              </div>

              {/* Additional Info */}
              <div className="mt-6 text-sm text-gray-600">
                <div className="flex justify-between mb-2">
                  <span>Start Time:</span>
                  <span>{new Date(product.startTime).toLocaleString()}</span>
                </div>
                <div className="flex justify-between mb-2">
                  <span>End Time:</span>
                  <span>{new Date(product.endTime).toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span>Total Stock:</span>
                  <span>{product.stock}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BiddingDetailPage;
