    setPurchaseResult(null);
    setUserPurchaseStatus(null);
    setLoading(false);

    // Set new username
    setUsername(newUsername);

    // Check if the new user has already purchased
    if (newUsername.trim()) {
      try {
        const response = await fetch(`http://localhost:3001/api/flash-sale/purchase/${newUsername.trim()}`);
        if (response.ok) {
          const data = await response.json();
          setUserPurchaseStatus(data);
        }
      } catch (error) {
        console.error('Failed to check user purchase status:', error);
      }
    }

    // Start onboarding sequence after a short delay
    setTimeout(() => {
      // Step 1: Highlight username input and center it
      const usernameInput = document.getElementById('username-input');
      if (usernameInput) {
        // Add highlight class
        usernameInput.classList.add('onboarding-highlight');
        usernameInput.focus();

        // Force scroll to center the element perfectly
        usernameInput.scrollIntoView({ 
          behavior: 'smooth', 
          block: 'center',
          inline: 'center'
        });

        // Additional scroll adjustment to ensure perfect centering
        setTimeout(() => {
          const newRect = usernameInput.getBoundingClientRect();
          const viewportHeight = window.innerHeight;
          const elementTop = newRect.top;
          const elementHeight = newRect.height;
          const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

          // Calculate exact center position
          const targetScrollTop = currentScrollTop + elementTop - (viewportHeight / 2) + (elementHeight / 2);

          window.scrollTo({
            top: targetScrollTop,
            behavior: 'smooth'
          });
        }, 100);

        // Remove highlight after 2 seconds
        setTimeout(() => {
          usernameInput.classList.remove('onboarding-highlight');
        }, 2000);

        // Step 2: After username highlight, highlight buy button
        setTimeout(() => {
          const buyButton = document.getElementById('buy-button');
          if (buyButton) {
            // Add highlight class
            buyButton.classList.add('onboarding-highlight');
            buyButton.focus();

            // Force scroll to center the element perfectly
            buyButton.scrollIntoView({ 
              behavior: 'smooth', 
              block: 'center',
              inline: 'center'
            });

            // Additional scroll adjustment to ensure perfect centering
            setTimeout(() => {
              const newButtonRect = buyButton.getBoundingClientRect();
              const viewportHeight = window.innerHeight;
              const buttonTop = newButtonRect.top;
              const buttonHeight = newButtonRect.height;
              const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

              // Calculate exact center position
              const targetButtonScrollTop = currentScrollTop + buttonTop - (viewportHeight / 2) + (buttonHeight / 2);

              window.scrollTo({
                top: targetButtonScrollTop,
                behavior: 'smooth'
              });
            }, 100);

            // Remove highlight after 3 seconds
            setTimeout(() => {
              buyButton.classList.remove('onboarding-highlight');
